@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("te31.com") {
/* 컬러가 따로 지정되지 않은 모든 element의 color를 적당히 검은색으로 */
/* 이렇게 하면 안됨;;; 스타일의 컬러를 상속받은 것들까지 다 검정이 되어버림 */
/**:not([style~=color]) {color:#3e3e3e !important}*/
html {background-color:#fefefe !important;}
div#wrapper {background-color:#fefefe !important;}
div#wrapper::before {content:"";display:inline-block;width:5%;min-width:3px;} /* 좌지롱 해결 */

div#bbs {width:100% !important;}
div#bbs table tbody[style~=min-height] tr td[style~=padding] {display:none !important;}

body,table,tr,td {background-color:#fff !important;}
textarea {background-color:#fefefe !important;}

table[style="color:#eee;margin-bottom:5px;"][width="100%"][height="42"][cellspacing="0"][cellpadding="0"][align="center"] tbody tr td[style="width:30%;padding-left:17px;font-size:18px;font-weight:bold;color:#fff;letter-spacing:0;"] a[href*=".php?id="] {font-size:21px !important;font-weight:bold !important;letter-spacing: -1px;} /* 글 목록 페이지 최상단의 게시판 이름 */


span[style*="font-size:14px;color:#ffffaa;padding:0;"] {color: #495e88 !important;} /* 페이지 우상단 최근 리플 보여주기 */

span#div_chu_bbs div {color:#f0f0f0 !important;background-color:#ccc !important;padding:1px 19px 1px 19px !important} /* 글 보기 페이지에서 추천버튼 */

span[class="sm logout"][style="font-size:12px;"] {color:#303030 !important;} /*글 보기 화면에서 첨부이미지 있을 경우 첨부이미지 개수 */

span[title="제목"]{font-size:16pt !important;font-weight:bold !important;color:#303030 !important;} /* 글 보기 화면에서 글 제목 */

div[itemprop*="articleBody"] {color:#303030 !important;font-size:14px !important;line-height:20pt !important;} /* 글 보기 화면에서 본문 */

select,option,input {font-size:10pt !important;color:#303030 !important;background-color:#f3f3f3 !important } /* 투표 관련 */

div[itemprop="articleBody"] + br + p + div[style="padding:8px;font-size:11px;"] {color:#303030 !important;} /* 글 보기 화면에서 투표 마감일 */

td[style*="word-break:break-all;WORD-WRAP:break-word;"][align="right"] {color:#303030 !important;} /* 글 보기 화면에서 투표 선택지들 */

div[style*="border-right:1px #"] + span[style*="font:11px verdana;"] {color:#303030 !important;} /* 글 보기 화면에서 투표 선택지의 퍼센트, 투표인 수 */

input.submit[value="다시 투표하기"] {border:1px solid #eee !important;} /* 다시 투표 버튼 */


span.chu-up {background-color: #eee !important; border:0px solid #fff00000 !important;} /* 댓글 추천 버튼 */
span.chu-down {background-color: #eee !important; border:0px solid #fff00000 !important;} /* 댓글 비추 버튼 */
span.midbtn {
    color: #303030 !important;
    font-size: 10.5px !important;
    background-color: #eee !important;
    border: 1px solid #eee !important;
    padding: 2px !important; } /* 글 보기화면에서 작성자 ID 눌렀을 때 나오는 기능버튼들 */

table[style*="border-bottom:1px solid #000;"] {border:0px !important;} /* 글 보기화면의 본문 박스, 글 목록화면 하단 여백 등등 여러 border box */

div[id=contentmemo] + div[style*="height:240px"] {margin-top:40px !important;margin-bottom:30px !important;} /* 글 보기화면의 광고 및 다른 게시물 목록 박스의 위 아래 여백 */

div[id=contentmemo] + div div[style*="background-color:#0f151f;"] {background-color:#ffffff00 !important; width:0px !important;border:0px solid !important;} /* 글 보기화면 본문 아래 좌측의 광고 div */

div[id=contentmemo] + div div[style*="background-color:#0f151f;"] + div[style*="width:420px;"] {width:450px !important;border:0px solid !important;float:right !important;color:#303030 !important;} /* 글 보기화면 본문 아래 우측의, 작성자의 다른 게시물 목록 */


form[name="pollForm"] + div[style*="margin-top:100px;"] {margin-top:20px !important;} /* 투표가 있을 때의 글 보기화면의 광고 및 다른 게시물 목록 박스의 위 아래 여백 */

form[name="pollForm"] + div[style*="margin-top:100px;"] div[style*="background-color:#0f151f;"] {background-color:#ffffff00 !important; width:0px !important;border:0px solid !important;} /* 투표가 있을 때의 글 보기화면 본문 아래 좌측의 광고 div */

form[name="pollForm"] + div[style*="margin-top:100px;"] div[style*="background-color:#0f151f;"] + div[style*="width:420px;"] {width:450px !important;border:0px solid !important;float:right !important;color:#303030 !important;} /* 투표가 있을 때의 글 보기화면 본문 아래 우측의 작성자의 다른 게시물 목록 */


div[id=contentmemo] + div div[style*="background-color:#0f151f;"] + div[style*="width:420px;"] table tbody tr td {line-height:95% !important;} /* 작성자의 다른 게시물 목록 줄간격 */

form[name="pollForm"] + div[style*="margin-top:100px;"] div[style*="background-color:#0f151f;"] + div[style*="width:420px;"] table tbody tr td {line-height:95% !important;} /* 투표가 있을 때의 작성자의 다른 게시물 목록 줄간격 */

form[name="c_write2"] + table {display:none !important;} /* 댓글창 아래 글수정, 글삭제 버튼 사이의 빈 공간 */

span[style*="font-size:15px;color:#cccccc;"] img[src*="/rgr/"] + span[onclick="showPopupi();"] {
    color: #303030 !important;
    font-weight:bold !important;
    font-size:11.5pt !important;
    font-family: 'Malgun Gothic', '맑은고딕', 'Noto Sans KR', 'Apple SD Gothic Neo', 'Apple SD 산돌고딕 Neo', '나눔고딕', 'Nanum Gothic', '돋움', Dotum, 'Helvetica', sans-serif !important;} /* 글 보기화면에서의 글 작성자 닉네임 */

span[onclick*="showPopup("] {
    color:#303030 !important;
    font-weight:bold !important;
    font-family: 'Malgun Gothic', '맑은고딕', 'Noto Sans KR', 'Apple SD Gothic Neo', 'Apple SD 산돌고딕 Neo', '나눔고딕', 'Nanum Gothic', '돋움', Dotum, 'Helvetica', sans-serif !important;} /* 글 보기화면에서의 리플 작성자 닉네임 */

i[id^=gifb_button]{color:#303030 !important;} /* 본문의 GIF 재생버튼 */
i.material-icons[style*="font-size:37px"] {color:#303030 !important;} /* 본문의 GIF 재생버튼 옆에 있는 GIF 글자 */
i.material-icons[style*="font-size:37px"] + span[style*="font-size:11px;"] {color:#303030 !important;} /* GIF 파일의 Size */
i[id^=gif_button]{color:#303030 !important;} /* 댓글의 GIF 재생버튼 */

table[style*="background-image: url(bit.png"] {border:0px solid !important; padding:10px 5px  !important;} /* 댓글 텍스트박스 테이블 */

.textarea[name="memo"] {border:1px solid #c0c0c0 !important;} /* 댓글 쓰기 텍스트박스 */

input.myButton[accesskey="s"] {color:#c0c0c0 !important; border:1px solid #c0c0c0 !important;} /* 글 보기 화면 댓글 전송 버튼 */


form[name="write2"] div[style*="background-color:#161d25;"] {background-color:#ffffff00 !important; border:0px solid !important;} /* 글 쓰기 페이지 테두리 */

.textarea {font-size: 13px !important;color:#303030 !important;line-height: 140% !important;} /* 글 작성 텍스트박스 */

div#drag {border:1px solid #c0c0c0 !important;} /* 글 쓰기 페이지 첨부 이미지 썸네일 */

.input2 {
    font-size: 12pt !important;
    font-weight: bold !important;
    font-family: 'Malgun Gothic', '맑은고딕', 'Noto Sans KR', 'Apple SD Gothic Neo', 'Apple SD 산돌고딕 Neo', '나눔고딕', 'Nanum Gothic', '돋움', Dotum, 'Helvetica', sans-serif !important;
