@-moz-document url-prefix("http://tvhay.org"), domain("tvhay.org") {
/* Credit - tvhay.org Interface Remake
Thằng vọc css (Author): JAK
Facebook: https://www.facebook.com/wblekhoa (Lê Khoa)
Email: wblekhoa@gmail.com */

:root {
--darkC1:  #131313;
--darkC2:  #252525;
--darkC3: #1f1f1f;
--whiteC1: #efefef;
--whiteC2: #a8a8a8;
--whiteC3: #7d7d7d;
--mainC: #26A7ED;
}

body {
    background-image: none;
    background-color: var(--darkC1);
}

.container, #content {
    width: 100%;
    display: grid;
    grid-template-columns: 1fr;
    grid-template-rows: 1fr;
    justify-items: center;
}

.container #content {
    width: 100%;
    background-color: var(--darkC1, #131313);
}

.block .blocktitle {
    background-image: none;
    background-color: transparent;
}

.list-film .inner >a {
    display: block;
    height: calc(300px);
    overflow: hidden;
}

ul.list-film li img {
    background-color: #252525;
    background-image: none;
    width: auto;
    height: calc(350px);
    position: relative;
    top: 50%;
    left: 50%;
    transform: translate(-50%,-50%);
    z-index: 1;
}

#page-watch .block {
	margin: auto;
}

/* Noi bat - movie hot */
#movie-hot {
    margin-top: 7%;
    width: 90vw!important;;
    max-width: 1280px;
    height: calc(55vh)!important;
}

#movie-hot .listfilm li {
    width: calc(200px)!important;
    height: calc(400px)!important;
    margin-right: 50px;
}

#movie-hot .listfilm .overlay {
    z-index: 2;
    position: absolute;
    bottom: 0;
    left: 0;
    height: auto;
    width: 100%;
    background-color: var(--darkC1);
    background-image: none;
}

#movie-hot .listfilm .name {
    margin-top: 20px;
    margin-bottom: 15px;
    font-weight: bold;
    font-size: 28px;
    line-height: 1.5;
    min-height: 87px;
    text-align: left;
    color: var(--whiteC1);
}

#movie-hot .listfilm .name2 {
    text-align: left;
    color: var(--whiteC2);
}

ul.list-film li .status, #movie-hot .listfilm .status {
    font-size: 12px;
    left: 0px;
    top: 0px;
    padding: 10px 15px 11px;
    position: absolute;
    border-radius: 0px;
    font-weight: normal;
    letter-spacing: 1px;
    z-index: 1;
}

#movie-hot .listfilm img {
    height: auto;
    width: 100%;
}

#movie-hot .listfilm .name a:hover, #movie-recommend ul.list li a:hover, #movie-recommend .col2 .blockbody li:hover span, #movie-recommend .col2 .blockbody li:hover a {
    color: var(--mainC);
}

#movie-hot .next, #movie-hot .prev {
    height: 60px;
    opacity: 0.8;
    position: absolute;
    top: 40%;
    transform: translateY(-50%);
    width: 47px;
    z-index: 5;
}

#movie-hot .next {
    background-position: -121px -69px;
    right: 30px;
}

#movie-hot .prev {
    background-position: -75px -68px;
    left: 20px;
}

/* menu */
#wrapper {
    position: fixed;
    width: 100%;
    background-color: var(--darkC3);
    z-index: 100;
}

#header {
    padding-top: 5px;
    width: auto;
    margin: auto;
    height: auto;
    display: table;
    background-color: var(--darkC3);
}

#nav {
    margin-top: 1px;
    padding-left: 5px;
}

#nav li a {
    height: 29px;
    width: auto;
    float: none;
    font-size: 12px;
    text-transform: none;
    color: var(--whiteC1);
}

#nav .sub-menu {
    display: none;
    position: absolute;
    width: 480px;
    background-color: var(--darkC3);
    background-image: none;
    left: 0;
    top: 29px;
    list-style: none;
    opacity: 1;
    border-radius: 0 0 2px 2px;
    padding: 5px;
    padding-top: 10px;
    padding-bottom: 5px;
    border: none;
    border-top: 0;
}

#nav .sub-menu li a  {
    color: var(--whiteC1);
}

#nav .sub-menu li a:hover {
    border-radius: 4px;
}

#search .keyword {
    width: 240px;
    color: var(--whiteC2);
    padding: 4px 15px 3px;
    background-color: var(--darkC1);
    border: none;
    box-shadow: none;
    font-size: 12px;
}

