/* Month*/
.climate-header-fixed__wrapper {background: #222;}
.radio-button_theme_yellow {background-color: #333;}
.radio-button_theme_yellow .radio-button__radio_checked_yes {background-color: #555;}
.radio-button_theme_yellow .radio-button__radio_link_yes > .radio-button__text, .radio-button_theme_yellow .radio-button__radio_link_yes > .radio-button__text .link {color: #ccc !important;}

.climate-calendar__row {border-color: #333;}
.climate-calendar-day__day-title, .climate-calendar-day__day {color: rgba(255,255,255,.5);}
.climate-calendar-day__temp-day {color: #ccc;}
.climate-calendar-day__temp-night {color: rgba(255,255,255,.7);}
.climate-calendar-day__history-icon {filter: invert(1);}

.climate-calendar-day__detailed-container-center {background: #222; box-shadow: 0 12px 24px 0 rgba(255,255,255,.16),0 0 0 1px rgba(255,255,255,.08);}
.climate-calendar-day__detailed-day, .climate-calendar-day__detailed-basic-temp-day, .climate-calendar-day__detailed-feels-like-value, .climate-calendar-day__detailed-data-table-cell, .climate-calendar-day__detailed-climate-temp-day {color: #ccc;}
.climate-calendar-day__detailed-basic-temp-night, .climate-calendar-day__detailed-climate-temp-night {color: rgba(255,255,255,.7);}
.climate-calendar-day__detailed-data-table-cell_value_yes .icon-abbr, .climate-calendar-day__detailed-climate-title {color: rgba(255,255,255,.6);}
.climate-calendar-day__detailed-feels-like {color: rgba(255,255,255,.5);}
.climate-calendar-day__detailed-climate {border-color: #333;}
.climate-calendar-day__detailed-data-table-cell_icon_yes .icon {filter: invert(1);}
.climate-calendar-text-bottom {color: rgba(255,255,255,.6);}

.climate-calendar-container__content-section, .climate-month-additional__content-item_position_left, .content__section {border-color: #333;}
.climate-month-additional-diagram__svg-path[fill="#febc00"] {fill: #cc9600 !important;}
.climate-month-additional-diagram__svg-path[fill="#2684d2"] {fill: #1d67a3 !important;}
.climate-month-additional-diagram__svg-path[fill="#8ebfe2"] {fill: #68aad9 !important;}
.climate-month-additional-diagram__legend-counter_color_sunny {background-color: #cc9600;}
.climate-month-additional-diagram__legend-counter_color_rainy {background-color: #1d67a3;}
.climate-month-additional-diagram__legend-counter_color_other {background-color: #68aad9;}
.climate-month-additional-param__value {color: #ccc;}
.climate-month-additional-param__description-units, .climate-month-additional-param__description-name {color: rgba(255,255,255,.5);}

.climate-graph__axis-x {border-color: rgba(255,255,255,.06);}
.climate-graph__axis-x-item-separator {border-color: rgba(255,255,255,.12);}
.climate-graph__axis-x-item-separator-text, .climate-graph__axis-x-item-wind, .climate-graph__axis-x-item-text, .climate-graph__axis-x-item-text-value-title, .climate-graph__axis-x-item-temp-title {color: rgba(255,255,255,.5);}
.climate-graph-temperature__axis-y line {stroke: #333 !important;}
.climate-graph-temperature__axis-y-text {fill: rgba(255,255,255,.5) !important;}
.climate-graph__axis-x-item-text_current_day_yes {color: #fff;}
.climate-graph__axis-x-item-text_holiday_yes {color: #f66049;}
.climate-graph__axis-x-item-text_current_day_yes {color: #fff;}

.climate-cities__wrapper-item_narrow_default .climate-cities__list-item, .climate-cities__wrapper-item_narrow_two-columns .climate-cities__list-item {border-right-color: #444;}
.climate-cities__wrapper-item_pos_left {border-right-color: #444;}
.climate-cities__country-name {color: rgba(255,255,255,.5);}

/* Temperature */
.weather-maps__layers.radio-button, .weather-maps-alert_theme_default {background: #222;}
.weather-maps-alert_closable_yes .weather-maps-alert__close {filter: invert(1);}
.weather-maps-alert__contraindications {background-color: #333;}

.map-button {background-color: #222;}
.map-control__icon {filter: invert(1);}

.radio-button_theme_yellow.radio-button_size_40, .radio-button_theme_yellow.radio-button_size_40 .radio-button__radio {background: #222;}
.radio-button_theme_yellow.radio-button_size_40.radio-button_theme_yellow .radio-button__radio_checked_yes {background-color: #555;}
.radio-button .radio-button__radio .link .radio-button__text:before {filter: invert(1);}

.balloon {background: #222; color: #ccc;}
.popup3_theme_default .popup3__content {background: #222; color: #ccc;}
.popup3__tail {background: #222;}
.map-popup__location {color: #888;}
.map-popup__param-title {color: #ccc;}
.map-popup__params .map-popup__param-value {color: #aaa;}
.icon_wind {background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" enable-background="new 0 0 11 11" preserveAspectRatio="xMinYMid" viewBox="0 0 11 11"><path d="M5.5 1L9 10 5.5 7.796 2 10z" fill="%23888"/></svg>');}
.link_theme_blue:hover {color: #e85253;}

.popup_theme_ffffff .popup__tail:after, .popup_theme_ffffff:before {background: #222;}
.legal-popup-content__data-copyright {background: #333;}
.legal-popup-content__legal-link .link:hover {background: #333; color: #ccc !important;}
.b-page_type_maps .popup_content_legal .popup__tail:after {background: #333;}
.legal-popup-content__data-copyright {color: #888;}

.timeline {background-color: #222;}
.timeline__arrow_direction_left {border-right-color: #333;}
.timeline__arrow_direction_right {border-left-color: #333;}
.timeline__arrow::before {filter: invert(1);}
.timeline .timeline-item_prev {color: #ccc;}
.timeline-item_type_precipitation {color: #ccc;}
.timeline-item_type_temperature {color: #ccc;}
.timeline__current {background: #333;}

/* Compare */
.compare-cities-self__link {color: #ccc !important;}
.compare-cities-self__arrow {border-color: #ccc;}

.climate-header-fixed {box-shadow: 0 8px 16px 0 rgba(0,0,0,.12),0 0 1px 1px rgba(255,255,255,.12);}
.b-page_type_compare .paranja {background: #222;}
.compare-cities-sidebar {background-color: #333;}
.compare-cities-suggest {background: #333;}
.compare-cities-suggest .search2 .input.input_theme_normal .input__box::before {border-color: #444;}
.compare-cities-suggest .search2 .input__box::after, .input_size_m .input__clear {filter: invert(1);}
.compare-cities-suggest .search2 .input__hint, .compare-cities-how-to__text {color: #ccc;}
.compare-cities-suggest__popup .suggest2__content {background: #222;}
.suggest2-item_selected_yes {background: #444;}
.compare-cities-table__concealer, .compare-cities-graph__highlight-container::before {background: #222;}
.compare-cities-graph__popup {color: rgba(255,255,255,.5);}
.swiper4__fadeout {background-image: linear-gradient(to left,#222,rgba(34,34,34,.85) 9%,rgba(34,34,34,.71) 19%,rgba(34,34,34,.57) 29%,rgba(34,34,34,.44) 39%,rgba(34,34,34,.3) 52%,rgba(34,34,34,.19) 65%,rgba(34,34,34,.08) 73%,rgba(34,34,34,.04) 85%,rgba(34,34,34,0));}
.swiper4__arrow {filter: invert(1);}
.swiper4__button-bg {background-image: radial-gradient(50% 50%,#222 50%,rgba(255,255,255,0) 100%);}
.compare-cities-table__section_header .compare-cities-table-row_initialized_yes .compare-cities-table-row__cell_highlight-on-hover_yes:hover {box-shadow: 0 12px 24px 0 rgba(255,255,255,.16),0 0 0 1px rgba(255,255,255,.08);}
.icon_trash, .compare-cities-table__icon_arrow-down {filter: invert(1);}
.compare-cities-table__section:nth-child(n+2) {border-color: #333;}

