@-moz-document url-prefix("https://duelingnexus.com/game/") {
@font-face {
    font-family: 'Source Sans Pro';
    src: url('https://raw.githubusercontent.com/sefeiba/duelingnexus/master/SourceSansPro-Regular.ttf') format('truetype');
    font-style: normal;
    text-rendering: optimizeLegibility;
}

body {
    margin: 0;
    overflow: hidden;
    color: #f0f0f0;
    font-family: 'Source Sans Pro', sans-serif;
    font-size: 14px;
}

html {
    background: url("/*[[background]]*/") no-repeat center center fixed !important;
    background-size: cover !important;

    /*
    background-position: 2.5% 50%;
    background-size: 120%;
    */
}
  
/*testing*/
.game-selection-card-image {
    transition: 0.1s;
}

.game-selection-card-image:hover {
    box-shadow: 0 0 2px 0 white;
}
/*testing*/

#ci-ext-misc-buttons {
    background-color: rgba(0, 0, 0, 0.8);
}

#game-selection-window {
    width: 74%;
    margin-left: 25.5%;
    margin-right: 0px;
    padding-right: 0;
    padding-left: 0;
}

#game-container {
    margin: 24px 16px 0px 16px;
    margin: 0px 0px 0px 0px;
}

#game-column {
    margin: 0px 0px 0px 0px;
    padding-top: 24px;
    height: 100vh;
    background-color: rgba(0, 0, 0, 0.5);
}

#game-chat-area {
    left: 0px;
    bottom: 0px;
    width: 25%;
}

#game-chat-textbox {
    border-radius: 5px;
    color: white;
    border: 0px solid white;
    box-shadow: inset 0 0 1px 0px white, 0 0 2px 0px white;
    text-shadow: 0 0 15px white;
    margin-left: 10px;
    margin-right: 10px;
    margin-bottom: 10px;
    padding-left: 10px;
    width: calc(25vw - 20px);
}

#game-chat-content {
    background-color: transparent;
    margin-left: 10px;
    margin-right: 10px;
}

.game-field-zone {
    position: relative;
    border: 0px;
    background-size: cover !important;
}

.game-field-zone:hover {
    border-radius: 0.4vw;
    box-shadow: inset 0 0 1.4vw #2e86de, 0 0 0.1vw #2e86de;
