@-moz-document url-prefix("https://duelingnexus.com/editor/") {
@font-face {
    font-family: 'Source Sans Pro';
    src: url("https://rawcdn.githack.com/sefeiba/duelingnexus/a6c19a039ab239895bc4f29ae453b9c481a44efe/yu_gi_oh__5d_s___city_square_bg_by_maxiuchiha22_dd6elm6.jpg") format('truetype');
