@-moz-document domain("barnesandnoble.com") {
.icon-lock:before,
.icon-cart:before,
.icon-search-2:before {
    content: ""!important;
}

.icon-search-2:before {
	background: url(https://i.ibb.co/Gxz13Q5/search-22x22.png) no-repeat!important;
	box-sizing: border-box!important;
	width: 25px!important;
	height: 25px!important;
	padding-left: 22px!important;
}

.icon-cart:before {
	background: url(https://i.ibb.co/x65LZ2L/shopping-cart-empty-22x22.png) no-repeat!important;
	box-sizing: border-box!important;
	width: 25px!important;
	height: 25px!important;
	padding-left: 22px!important;
}

.icon-lock:before {
	background: url(https://i.ibb.co/9bS3nYd/lock20x0.png) no-repeat!important;
	box-sizing: border-box!important;
	width: 25px!important;
	height: 25px!important;
	padding-left: 22px!important;
}


/* Contents _______________________________________________________________*/

