    /*INIT*/

        /*grid*/
    header {
        overflow: hidden;
        height: unset !important;
        max-height: 46px;
        transition: all .5s!important;
    }
    header:hover {
        max-height: 200px;
    }
    .header__inner {
        flex-direction: column;
    }
    .header__inner > *,
    .header__inner > * > * {
        display: flex !important;
        justify-content: center;
    }
    .header__navMenu > * {
        max-width: 33.333%;
    }
    .header__search,
    .headerSearch
    {
        width: 100%
    }
    .l-container.l-content {
        box-sizing: border-box;
        padding: 46px 10px;
        overflow: hidden;
        width: 100%;
    }



    /*LEFT RIGHT AREA*/
    
        /*main*/
    .l-main,
    .l-about-main
    {
        margin: 0 !important;
        transition: opacity .5s;
        opacity: 1;
    }
    
    *:hover>.l-main:not(:hover),
    *:hover>.l-about-main:not(:hover)
    {
        opacity: .1;
    }
    
        /*right*/
    .l-sidebar-right
    {
        margin-right: -320px !important;
        border-left: solid 1px currentColor;
        padding: 0 10px;
        min-height: 90vh;
        
        transition: margin .5s; 
                                            
		animation: right-menu-load .5s cubic-bezier(0.21, -0.4, 0.77, 1.38);
		animation-delay: 3s
    }
    .l-sidebar-right:hover
    {
        margin-right: -10px !important
    }
    
        /*left*/
    .l-fixed-left
    {
        margin-left: -245px !important;
        border-right: solid 1px currentColor;
        padding: 0 10px;
        min-height: 90vh;
        
        transition: margin .5s;
        z-index: 10;
    }
    .l-fixed-left:hover
    {
        margin-left: -10px !important
    }
    
        /*top*/
    .l-fixed-top
    {
        top: -5px !important;
    }
    


    /*PAGE*/
    
        /*playlist edit*/
    .baseFields {
        display: flex;
        flex-wrap: wrap;
        justify-content: space-around;
        padding: unset;
        max-width: 600px;
        margin: auto;
    }
    .baseFields>*
    {
        position: relative;
        width: 100%;
    }
    .baseFields__image {
        width: auto;
    }
    .editPlaylistBasicTab__fields {
        margin: 0;
    }
    
        /*user page*/
    .userInfoBar
    {
        justify-content: space-around;
    }
    
    .profileHeaderBackground__visual 
    {
        background-position: center;
        background-size: cover;
    }
    
        /*song page up*/
    .fullListenHero, .fullHero {
        width: 100%;
        height: auto;

        margin: 0;
    }
    .fullHero__foreground {
        display: flex;
        position: unset;
        width: 100%;
    
        padding: 0 20px !important;
        padding-top: 50px !important;
        
        align-items: center;
        flex-direction: column;
    }
    .fullHero__foreground > * {
        position: unset;

        width: auto;
        max-width: 100%;
        height: auto;
        
        padding: 5px 0;
        text-align: center;
        z-index: 1;
    }
    .fullHero__titleButton,
    .fullListenHero .soundTitle__playButton {
        display:none;
    }
    .fullHero__playerArea {
        width: 100%;
    }
    .fullHero__artwork {
        width: 250px;
        height: 250px;
    }
    .fullHero__waveform {
        height: unset;
    }
    .fullHero__waveform .waveformWrapper {
        height: 100px
    }
    .fullHero__waveform>.g-box-full:empty{
        display:none;
    }
    
    .sound__footer.noDivider {
    display: flex;
    flex-wrap: wrap-reverse;
    height: unset !important;
}

.sound__footer.noDivider>* {
    position: relative;
}
    
        /*song page down*/
    .listenEngagement
    {
        padding: 10px !important;
        width: 100% !important;
    }
    .l-about-rows
    {
        padding: 0 !important;
    }
    .l-about-row
    {
        display: flex;
        flex-wrap: wrap;
    }
    .l-about-row>*
    {
        display: flex;
        justify-content: space-around;
        position: relative !important;
        padding: 0 !important;
        width: 100% !important; 
    }
    
      /*playlist page */
    .l-hero-content
    {
        padding-right: 0;
    }

    /*VISUAL*/

    .backgroundGradient 
    {
        position: absolute;
        width: 100%;
        z-index: 0;
        top: 0;
        bottom: 0;
    }



    /*PLAY CONTROLS*/
    .playControls {
        visibility: visible;
    }
    .playControls__elements {
        flex-wrap: wrap-reverse;
        min-width: 340px;
        max-width: 550px;
    }
    .playControls__soundBadge > * {
        width:100%;
    }
    .playControls__soundBadge {
        width: 100%;
    }
    .playControls__queue 
    {
        bottom: 100px;
        right: 0;
        max-height: 75vh;
        max-width: 100vw;
    }
    .playControls__queue *
    {
        max-width: 100%;
        max-height: 100%;
    }
}
}

