    height: 42px;
    line-height: 42px;
    border-bottom: 1px solid #6495ed96;
    background-color: #f3f3f3;
}

/* Left pane */
.rc-SecondaryNavTogglePanel {
    box-shadow: none;
    border-right: 1px solid #6495ed96;
    border-left: 1px solid #6495ed96;
}

