@-moz-document url-prefix("https://www.baidu.com/s?") {
/* 搜索结果背景图片 */
#wrapper {
	background-image: url("/*[[background-image]]*/") !important;
	background-repeat: no-repeat;
	background-attachment: fixed;
	background-size: cover;
}

#s_tab {
	margin-bottom: 22px;
	background: rgba(255, 255, 255, .75) !important;
}

#s_tab a {
	transition: all .25s;
}

.c-border {
	padding-top: 0 !important;
	box-shadow: none !important;
}

.c-gap-bottom-small.f13,
table.result-op,
.result.c-container,
.result-op.c-container {
	width: 560px;
	background: rgba(255, 255, 255, .8);
	padding: 24px;
	border-radius: 3px;
	transition: all .15s;
}

.result.c-container:hover,
.result-op.c-container:hover {
	box-shadow: 5px 5px 5px rgba(0, 0, 0, .15);
}

/* 搜索结果下划线 */
.t a {
	font-size: 17px !important;
	font-weight: 500;
}

.t a,
.t a em {
	text-decoration: none !important;
}

.t a {
	transition: all .1s ease-out;
	position: relative;
	box-sizing: content-box;
	padding-bottom: 2px;
	color: #3951b3;
}

.t a:hover {
	color: #4e6ef2;
}

.t a::after {
	transition: all .2s ease-out;
	content: '';
	position: absolute;
	background: #4e6ef2;
	bottom: 0;
	left: 0;
	height: 2.5px;
	width: 0;
	border-radius: 3px;
}

.t a:hover::after {
	width: 100%;
}

/* 去除推广 */
#content_right,
#rs,
#foot,
.head_nums_cont_outer.OP_LOG,
.wenda-abstract-showurl-video-new-style,
.c-recommend {
	display: none;
}
}

