@-moz-document regexp('https:\/\/.*hackmd\.io\/.*')
{
    /*--------------- view ---------------*/
    body[style],
    body[style*="background-color: white;"] {
        background-color: #363b41 !important;
    }

    body {
        color: #363b41;
        background: #363b41;
    }

    .ui-view-area,
    .markdown-body,
    .ui-content {
        background: #363b41;
        color: #abb2bf;
    }

    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    p {
        color: #b8bfc6
    }

    /* form */
    .form-control {
        background: #333;
        color: #fff;
        border-color: #8e8e8e;
    }

    .form-control::placeholder,
    .form-control::-webkit-input-placeholder,
    .form-control:-moz-placeholder,
    .form-control::-moz-placeholder,
    .form-control:-ms-input-placeholder {
        color: #eee;
    }

    /*--------------- navbar ---------------*/
    .header {
        background-color: #2e3033;
        border-color: #2e3033;
    }

    .navbar {
        background-color: #2e3033;
        border-color: #2e3033;
    }

    .navbar a {
        color: #eee !important;
    }

    .navbar .btn-group label {
        background-color: #2e3033;
        color: #eee;
        border-color: #555;
    }

    .navbar .btn-group label.btn-default:focus,
    .navbar .btn-group label.btn-default:hover {
        background-color: #2a2a2a;
        color: #eee;
        border-color: #555;
    }

    .navbar .btn-group label.active {
        background-color: #555;
        color: #eee;
        border-color: #555;
    }

    .navbar .btn-group label.active:focus,
    .navbar .btn-group label.active:hover {
        background-color: #555;
        color: #eee;
        border-color: #555;
    }

    .navbar-default .btn-link:focus,
    .navbar-default .btn-link:hover {
