@-moz-document regexp("^https:\\/\\/jira.*\\/RapidBoard\\.jspa.*$") {
dd .js-quickfilter-button {
    font-weight: 600;
    color: gainsboro;
}

dl:first-of-type .ghx-cursor-help {
    display: none;
}
.ghx-controls-work {
    left: 33%;
}

.ghx-columns .ghx-column {
    background: transparent;
}

.ghx-column-headers .ghx-column {
    padding: 3px 9px;
    margin-top: -15px;
}

.ghx-column-headers .ghx-column-title {
    font-size: 28px;
}

.ghx-column-header-content {
	height: 28px;
}

.ghx-grabber {
    width: 8px;
    box-shadow: 2px 0 4px 0 rgba(9,30,66,0.31);
}

.ghx-issue {
    padding: 4px 16px 4px 24px;
}

.ghx-issue .ghx-issue-content {
    min-height: inherit;
}

.ghx-issue .ghx-key-link {
    color: palevioletred;
    font-size: 20px;
    font-weight: 600;
    line-height: 1.66666667;
    margin-left: 28px;
}

.ghx-issue .ghx-type{
    position: absolute;
    height: 18px;
    width: 18px;
    left: 25px;
    top: 13px;
}

.ghx-issue .ghx-type img{
    height: 18px;
    width: 18px;
}

.ghx-issue .ghx-flags .ghx-priority {
    display: none;
}

.ghx-issue .ghx-flags {
    position: absolute;
    right: 112px;
    top: 16px;
}

.ghx-issue .ghx-end{
    position: absolute;
    right: 50px;
    top: 13px;
}

.aui-badge, aui-badge {
    font-size: 16px;
    padding: 6px 12px;
    color: teal;
    box-shadow: inset 2px 0 4px 0 rgba(9,30,66,0.31);
}

.ghx-issue .ghx-extra-fields {
    margin: 3px 0 5px 30px;
    font-weight: 600;
    color: steelblue;
    font-size: small;
    justify-content: space-between;
    display: flex;
}

.ghx-issue .ghx-extra-fields .ghx-extra-field-row {
    display: inline-block;
}

.ghx-fa {
	color: white;
}

.ghx-issue .ghx-card-footer {
    margin: 1px;
}

.ghx-issue .ghx-days{
    margin-left: 20px;
}
}

