    --midgrey: hsla(0, 0%, 12%, 1) !important;
    --midgrey2: #282729!important;
    --lightgrey: hsl(0, 0%, 20%) !important;
    --bordercolor: hsla(0, 0%, 30%, 1) !important;
    --textcolor: hsla(0, 0%, 80%, 1) !important;
