	background: var(--lightgrey);
}

header .sidebar dl dd a {
	color: var(--textcolor);
}

header .sidebar dl {
	border-bottom: 1px solid var(--bordercolor);
}

header .sidebar dl dt a, input {
	color: var(--textcolor);
}

header .sidebar dl dt a:hover {
	background: var(--greybg);
}

