@-moz-document domain(surviv.io)
{

  #background
  {
    background-image: url(https://vignette.wikia.nocookie.net/animal-jam-clans-1/images/7/7d/Blue_mountains_animation_by_grayamaroq-d9vbn8y.gif/revision/latest?cb=20160721075412) !important;
  }

