  {
    background-image: url(https://upload.wikimedia.org/wikipedia/commons/7/7f/Surviv.io.png) !important;
  }

  #start-top-left
  {
    visibility: visible !important;
  }

  #featured-streamers
  {
    visibility: visible !important;
  }

  #start-bottom-left
  {
    visibility: visible !important;
  }

  #start-bottom-middle
  {
    visibility: visible !important;
  }

  .menu-option
  {
    border-radius: 0px !important;
    border-color: #40436d !important;
  }

  .btn-green
  {
    border-bottom: 0px !important;
    background: #6c70b7 !important;
    color: #40436d !important;
    box-shadow: inset 0 -2px #202020;
  }

  .btn-team-option
  {
    border-bottom: 0px !important;
    background: #6c70b7 !important;
    color: #40436d !important;
    box-shadow: inset 0 -2px #202020;
  }

  .account-details-button-wrapper
  {
    border-bottom: 0px !important;
    background: #6c70b7 !important;
    color: #40436d !important;
    box-shadow: inset 0 -2px #202020 !important;
  }

  #btn-pass-locked
  {
    border-bottom: 0px !important;
    background: #6c70b7 !important;
    color: #40436d !important;
    box-shadow: inset 0 -2px #202020;
  }

  #btn-customize
  {
    border-bottom: 0px !important;
    background: #6c70b7 !important;
