  }

  #btn-help
  {
    border-bottom: 0px !important;
  }
}

@-moz-document domain("grouple.co"), domain("readmanga.live"), domain("mintmanga.live"), domain("doramatv.live"), domain("librebook.me"), domain("findanime.net"), domain("selfmanga.ru"), domain("selflib.me"), domain("mose.live") {
/* grouple all sites theme v.1.1 by krravvi */

/* ============ NONE ============ */

div.co_ba, .co_ba.middle_ba,
div.co_ba.reader-comment-ba,
div.co_ba.top_ba,
div.co_ba.right_ba,
div.co_ba.reader_twitts_ba {
    display: none !important;}

br + br {display: none !important;}

.left-brand, .right-brand div {display: none !important;}

/*============ BODY ============ */

body {
    font-family: "Calibri" !important;
    font-size: 15px;
    line-height: 20px;
    letter-spacing: .4px;
    background: #F6F6F6 !important;
    color: #595959;}

a {
    color: #8592B2;
    border: none !important;}

a:hover, .listSelected, .js-link.current {
    transition: all .2s linear !important;
    border: none !important;
    background: transparent !important;
    color: #909090 !important;
	opacity: .85 !important;}

p {text-indent: 0px; text-align: left;}

.fa {text-shadow: none !important; text-align: center;}

/*============ BODY: BOX ============ */

div.container.pageBlock {
    top: 60px;
    box-shadow: none !important;
    border: 1px solid #EEE;
    border-radius: 3px;
    margin: 0 auto 5px auto;}

.p-control {box-shadow: none !important;}

/*============ BODY: BOX - COMMENTS ============ */

.twitter .cm .mess {
    margin-left: 0; 
    line-height: 18px;}

.twitter .cm .comm {
    background: #FAFAFA;
    border-left: 3px solid #EEE;
	margin: 10px;
    padding: 8px 10px;
	border-radius: 3px;
	font-size: 13px;
    letter-spacing: .8px;
	box-shadow: 1px 0px 1px 1px rgba(0,0,0,.1);}

.twitter .cm .children {
    border-radius: 3px;
    padding: 2px;
    margin: 5px;
    border-left: none;}

.twitter .cm .children > div.comm {
    background: #fff !important;
    border-left: 3px solid #E8E8E8;}

.twitter .cm .comm a {
	font-size: 12px;
	color: #9CA6C0;
    font-weight: bold;}

.twitter .cm .contr {opacity: .6 !important; cursor: default;}
.twitter .cm .contr:hover {opacity: 1 !important;}

.twitter .cm .comm > span > span.js-link.fa.fa-reply {
    font-size: 12px;
    color: #909090;}

.twitter .cm .comment_good .votes, .twitter .cm .comment_vgood .votes {
    color: #97C1A2 !important; font-size: 12px;}

.twitter .cm .contr .fa-caret-up,
.twitter .cm .contr:hover .fa-caret-up {
    color: #97C1A2 !important; font-size: 14px;}

.twitter .cm .comment_bad .votes, .twitter .cm .comment_vbad .votes {
    color: #E79379 !important; font-size: 12px;}

.twitter .cm .contr .fa-caret-down,
.twitter .cm .contr:hover .fa-caret-down {
    color: #E79379 !important; font-size: 14px;}

#twitts > h5 > span > i.fa.fa-step-forward.text-success.fa-rotate-90.fa-lg {
    margin: 0 0 0 -15px;
    font-size: 14px;
	color: #9CA6C0;}

/*============ *** ============ */

#twitts > div.cm_0.cm {
    max-height: 480px;
    overflow-y: auto;}

#twitts > div.cm_0.cm::-webkit-scrollbar {width: 8px;}
#twitts > div.cm_0.cm::-webkit-scrollbar-track {background: #F2F3F7;}
#twitts > div.cm_0.cm::-webkit-scrollbar-button {background: #F2F3F7; height: 3px;}
#twitts > div.cm_0.cm::-webkit-scrollbar-thumb {
    height: 15%;
    background-color: #D6D9E6;
    border-radius: 4px;
    border: 2px solid #F2F3F7;}

/*============ BODY: BOX - TRANSLATOR ============ */

.translator-control {
    margin: 0 0 10px 0;
    padding: 10px;
    border-bottom: 1px solid #EEE;
    text-transform: uppercase;
    font-size: 15px;}

/*============ BODY: BOX - ARROW ============ */

.arrow-left, .arrow-right {background-image: none;}
.arrow-left:hover:before, .arrow-right:hover:before {
    transition: all .3s linear !important; 
    color: #DADADA;}

.arrow-left:before {
    content: "\f053";
    font-family: "FontAwesome", "Font Awesome 5 Pro" !important;
    color: #E8E8E8;
    font-size: 55px;}

.arrow-right:before {
    content: "\f054";
    font-family: "FontAwesome", "Font Awesome 5 Pro" !important;
    color: #E8E8E8;
    font-size: 55px;}

/*============ SIDE TOP: LEFT ============ */

.navbar.navbar-default {
    z-index: 1000;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 60px;
    background: #404758;
    border-radius: 0px;
    padding: 10px 0 10px 0;
    opacity: .95;
    backdrop-filter: blur(1px) !important;}

.navbar-nav > li > a {padding-top: 8px; padding-bottom: 8px;}

.federation-nav {
    position: fixed;
    top: 54px;
    left: 0px;
    background: none;}

.federation-nav .container {
    height: auto;
    background: #404758;
    border-bottom: 3px solid #B9BED4;
	border-bottom-right-radius: 3px;
    border-bottom-left-radius: 3px;
    letter-spacing: 1px;
    opacity: .95;}

.federation-nav a {
    color: #FFF !important;
    font-size: 12px;
    font-weight: normal;
    text-transform: uppercase;
    margin: 0 2px;
    padding: 5px 7px;}

.federation-nav a:hover {
    background-color: #B9BED4 !important;
    color: #FFF !important; 
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
    opacity: 1 !important;}

.fa-caret-square-down:before,
.fa-caret-square-up:before,
.fa-caret-square-o-down:before,
.fa-caret-square-o-up:before {
    position: relative; 
    top: 0px; 
    left: 10px;
	color: #B9BED4; 
    font-size: 17px;}

.fa-caret-square-down:before,
.fa-caret-square-o-down:before {
    content:"\f13a"; font-family: "FontAwesome", "Font Awesome 5 Pro" !important;}

.fa-caret-square-up:before,
.fa-caret-square-o-up:before {
    content:"\f139"; font-family: "FontAwesome", "Font Awesome 5 Pro" !important;}

/*============ SIDE TOP: RIGHT ============ */

.navbar-default .navbar-nav > li > a {
    color: #FFF !important;
    font-size: 15px; 
    font-weight: normal; 
    letter-spacing: 1px;}

.navbar-default .navbar-nav > li > a:hover, 
.navbar-default .navbar-nav > li > a:focus {
    background-color: #404758 !important;
    color: #B9BED4 !important; 
	opacity: 1 !important;}

.navbar-default .navbar-nav > .open > a, 
.navbar-default .navbar-nav > .open > a:hover, 
.navbar-default .navbar-nav > .open > a:focus {
    background-color: #404758 !important;
    color: #B9BED4 !important;
	opacity: 1 !important;}

/*============ SIDE TOP: RIGHT - ICONS ============ */

#accountMenu > li > a > span.fa.fa-lg.fa-edit,
#accountMenu > li > a > span.fa.fa-lg.fa-bookmark,
#accountMenu > li > a > span.fa.fa-lg.fa-bookmark-o,
#accountMenu > li > a > i.fa.fa-lg.fa-comment, 
#accountMenu > li > a > i.fa.fa-lg.fa-comment-o,
#accountMenu > li > a > span.fa.fa-lg.fa-comment,
#accountMenu > li > a > span.fa.fa-lg.fa-comment-o,
#accountMenu > li > a > span.fa.fa-lg.fa-bell,
#accountMenu > li > a > span.fa.fa-lg.fa-bell-o {
    background: #353B49;
    padding: 8px 0 0 2px;
    margin: -5px 0 0 0;
    border-radius: 3px;
    font-size: 16px;
    width: 28px;
    height: 28px;
    vertical-align: 2px;}

#accountMenu > li > a > i.fa.fa-lg.fa-shopping-cart.gold-status:before,
#accountMenu > li > a > span.fa.fa-lg.fa-shopping-cart:before {
    content: "\f09d";
    font-family: "FontAwesome", "Font Awesome 5 Pro" !important;
    font-size: 16px;
    color: #B9BED4;
    width: 28px;
    height: 28px;
    margin: 0 0 0 10px;}

#navbar > ul.nav.navbar-nav.navbar-right.hidden-xs.hidden-sm > li > a > span.fa.fa-question-circle.fa-lg {
    font-size: 17px;
    color: #B9BED4;
    margin: 0 0 0 -10px;}

/*============ SIDE TOP: RIGHT - DROP ============ */

.navbar-nav > li > .dropdown-menu {
    box-shadow: 0 5px 10px 0 rgba(0,0,0,.1);
    background: #FFF;
    border: 1px solid #DADADA;
    border-radius: 3px;
    font-size: 11px;
    text-transform: uppercase;
    letter-spacing: 1px;
    margin: 20px 0 0 0;
    padding: 8px;}

.dropdown-menu .divider {
    overflow: hidden;
    height: 1px;
    background-color: #EEE;
    margin: 5px 0;}

.navbar-nav > li > .dropdown-menu a {
    color: #595959 !important;
	opacity: 1 !important;}

.navbar-nav > li > .dropdown-menu a:hover {
    color: #BABABA !important;
	opacity: 1 !important;}

/*============ SIDE TOP: RIGHT - DROP - ICONS ============ */

.navbar-nav > li > .dropdown-menu a > .fa-fw {
    width: 35px; 
    padding: 5px;
    text-align: center;
	color: #9CA6C0 !important;}

#accountMenu > li.dropdown.open > ul > li > a > span {
    background: #F2F2F2;
    padding: 7px 0 0 1px;
    margin: 0 10px 0 0;
    border-radius: 3px;
    font-size: 14px;
    width: 24px;
    height: 24px;
    vertical-align: -1px;}

#accountMenu > li.dropdown.open > ul > li > a > span.fa.fa-lg.fa-fw.fa-heartbeat:before {
    content: "\f274";
    font-family: "FontAwesome", "Font Awesome 5 Pro" !important;}

/*============ SIDE SECOND BAR ============ */

.second-nav {
    position: relative;
    top: 60px;
	background: transparent;
	color: transparent;
    padding: 0;
	margin: 5px 0 -2px 0;}

