@-moz-document domain("readmanga.live"), domain("mintmanga.live"), domain("doramatv.live"), domain("librebook.me"), domain("findanime.net"), domain("selfmanga.ru"), domain("selflib.me"), domain("mose.live") {
/*============ SUB SITES ============ */

.rightContent {
    background: #FFF;
	border-left: 3px solid #F6F6F6;
    border-radius: 0px;
	letter-spacing: .1px;}

.main-page-cower img {
    margin-bottom: 10px;
    transition: all .2s linear !important;
    border: 1px solid #EEE;
    padding: 10px;
    border-radius: 4px;
    height: 400px;
    object-fit: none;
    filter: saturate(.8);}

/*============ SUB SITES: H5 ============ */

.rightContent h5 {
    font-size: 13px;
    font-weight: normal;
    text-transform: uppercase;
    background: #F6F6F6 !important;
    margin: 10px 0 10px -10px !important;
    padding: 10px !important;
    border-top-right-radius: 2px;
    border-bottom-right-radius: 2px;}

#mangaBox > div.rightContent > div:nth-child(2) > h5 {
    background: none !important;
    margin: 0 5px !important;
    padding: 0 !important;
	line-height: 20px;}

#mangaBox > div.rightContent > h5:nth-child(4),
#mangaBox > div.rightContent > h5:nth-child(7) {
    font-weight: bold;
    background: #F2F3F7 !important;}

/*============ SUB SITES: SOCIAL ============ */

div.rightBlock.social-share {
    padding: 8px 0 !important;
    margin: 8px 0 !important;
    border-top: 1px solid #EEE;
    border-bottom: 1px solid #EEE;}

div.rightBlock.social-share > h5 {
    background: none !important;
    margin: 0 5px !important;
    padding: 0 !important;
    line-height: 18px;}

div.social-likes.social-likes_light.social-likes_visible {
    padding: 5px 0 !important;
    margin: 0 !important;}

/*============ SUB SITES: LAST COMMENTS ============ */

#mangaBox > div.rightContent > div:nth-child(4) > div > div:nth-child(even) > a:nth-child(1),
#mangaBox > div.rightContent > div:nth-child(4) > div > div:nth-child(odd) > a:nth-child(1),
#mangaBox > div.rightContent > div:nth-child(5) > div > div:nth-child(even) > a:nth-child(1),
#mangaBox > div.rightContent > div:nth-child(5) > div > div:nth-child(odd) > a:nth-child(1),
#mangaBox > div.rightContent > div:nth-child(6) > div > div:nth-child(even) > a:nth-child(1), 
#mangaBox > div.rightContent > div:nth-child(6) > div > div:nth-child(odd) > a:nth-child(1),
#mangaBox > div.rightContent > div:nth-child(9) > div > div:nth-child(even) > a:nth-child(1),
#mangaBox > div.rightContent > div:nth-child(9) > div > div:nth-child(odd) > a:nth-child(1),
#mangaBox > div.rightContent > div:nth-child(10) > div > div:nth-child(even) > a:nth-child(1), 
#mangaBox > div.rightContent > div:nth-child(10) > div > div:nth-child(odd) > a:nth-child(1){
    font-weight: bold !important;
    color: #595959 !important;}

.lastTwitts .twitt {
    font-size: 11px;
    border-bottom: 1px dashed #DADADA;
    padding: 2px 0;}

.lastTwitts .twitt a {color: #757575 !important;}
.lastTwitts .twitt a:hover {color: #909090 !important;}

.lastTwitts .twitt:last-of-type {border-bottom: none;}

/***/

div.lastTwitts {
    max-height: 200px !important; 
    min-height: 35px !important;
    overflow-y: auto; 
    padding: 0 4px !important;
    background: #FAFAFA;
    border: 1px solid #DDD;
    border-radius: 3px;}

div.lastTwitts::-webkit-scrollbar {width: 7px;}
div.lastTwitts::-webkit-scrollbar-track {background: #FAFAFA;}
div.lastTwitts::-webkit-scrollbar-button {background: #FAFAFA; height: 3px;}
div.lastTwitts::-webkit-scrollbar-thumb  {
    height: 15%;
    background-color: #D6D9E6;
    border-radius: 4px;
    border: 2px solid #FAFAFA;}

/*============ SUB SITES: LINKS ============ */

div.rightContent > div > ul > li > a.screenshot.processed-screenshot:before {
    vertical-align: 2px;
    margin: 0 0 0 4px;}

div.rightContent > div > ul > li,
div.rightContent > div > ul > li > a {
    font-size: 14px; letter-spacing: .6px;}

div.rightContent > div > ul > li > a.person-link {color: #757575; line-height: 22px;}

/***/

.pageBlock .rightContent ul {max-height: 300px; overflow-y: auto;}
.pageBlock .rightContent ul::-webkit-scrollbar {width: 3px;}
.pageBlock .rightContent ul::-webkit-scrollbar-track {background: #F6F6F6;}
.pageBlock .rightContent ul::-webkit-scrollbar-thumb {
    background-color: #D6D9E6;
    border-radius: 6px;}
}

