data.css:
@-moz-document url-prefix("https://nitrogensports.eu") {
/** Nitro red: #bc2d2c **/
/** Nitro yellow: #ffb129 **/
body {
    color: #fff!important;
}
a {
    color: #bc2d2c;
}
a:focus, a:hover {
    color: #A92928;
}
.main {
    background-color: #262626!important;
}
.table-striped tbody>tr:nth-child(odd)>td, .table-striped tbody>tr:nth-child(odd)>th {
    background-color: #3D3D3D!important;
}
.table td, .table th {
    border-top: 1px solid #5a5e6d !important;
}

.chocolate-outer {
    background: #262626!important;
    border: 1px solid #5a5e6d !important;
    box-shadow: none!important;
}
.chocolate-content {
    border-top: none!important;
    background: #3D3D3D!important
}
.chocolate-header {
    background: #262626!important;
}

.chocolate-input-group, .chocolate-input-group-auto {
    background-color: #262626!important; 
    border: 1px solid #5a5e6d!important;
}
.chocolate-input-group i, .chocolate-button i, .chocolate-button-auto i {
    color: #a6a6a6;
}
.chocolate-textbox::placeholder {
    color: #a6a6a6!important;
}
.chocolate-button, .chocolate-button-auto {
    background-color: #262626!important; 
    border-bottom-color: #5c6070!important;
    border-top-color: #73788c!important;
    border-left: 1px solid #5c6070!important;
    border-right: 1px solid #5c6070!important;
}

/** Welcome **/
#landing-image {
    background-image: none!important;
}
#landing-logo-new {
    background-image: url('https://nitrogensports.eu/assets/images/logo_new.png');
    background-size: contain;
}
#landing-tagline-new {
    color: #dcdcdc;
}
#landing-greeting {
    color: #fff!important;
}
#landing-sub-greeting1 {
    color: #dcdcdc;
}
#landing-welcome-id {
    color: #ffb129!important;
}
#landing-welcome-box p {
   color: #dcdcdc; 
}
#landing-welcome-box p a {
    color: #bc2d2c!important;
}

/** Welcome moda **/
#modal-welcome {
    background: #3D3D3D!important;
}
#modal-welcome-logo {
    height: 45px;
    background-image: url('https://nitrogensports.eu/assets/images/logo_new.png');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
}


/** Bet page **/
#events-empty div {
    color: #fff!important;
}
#events-empty .sub {
    color: #dcdcdc!important;
}
.page-title-dark {
    color: #fff!important;
}
.page-title-dark small {
    color: #bc2d2c!important;
}
#find-games-title, #favourite-league, .favourite-league__container::before {    
    background-color: transparent;
}
#favourite-league.favourite-league--add, #favourite-league:hover:not(.animate) {
    background-color: #bc2d2c;
}
#favourite-league__label {
    display: none!important;
}

#period-tabs {
    border-bottom: 1px solid #6f6f6f !important; 
}
.period-tab a {
    color: #bc2d2c!important;
}
.period-tab a:hover {
    background: #3D3D3D!important;
    border: 1px solid #6f6f6f !important;
}

.period-tab.active a, .period-tab.active a:hover {
    background: #3D3D3D!important;  
    border: 1px solid #6f6f6f !important; 
    color: #fff!important;
}

#landing-stripe2 {
    background-color: #262626!important;
    border: none!important;
}
#landing-bitcoin-text {
    color: #fff;
}
#landing-bitcoin-text-small {
    color: #dcdcdc;
}
.landing-span-new {
    color: #dcdcdc;
}
.landing-span-header-new {
  color: #fff;  
}
#landing-stripe3 {
    background-color: #3D3D3D!important;
    border: none!important;
}
#landing-what-bitcoin-text, #landing-buy-bitcoin-text-small {
    color: #fff;
}
#landing-what-bitcoin-text-small, .landing-what-bitcoin-point-text {
    color: #dcdcdc!important; 
}
#landing-stripe4 {
    background-color: #262626!important;
    border: none!important;
}

/** Event search **/
#event-search {
    box-shadow: none!important;
}

/** Event **/
.event {
    border: 1px solid #5a5e6d !important;
    box-shadow: none!important;
}

.event-header {
    padding: 5px 0 5px 0!important;
    background: #262626!important;
}

.event-content {
    background: #3D3D3D!important;  
}
.event-add-button  {
    background-color: #bc2d2c !important;
    border-top-color: #C13B3A !important;
    border-bottom-color:#A92928 !important;
}


/** Betslip **/
#betslip-outer {
    background: #3D3D3D!important;  
    border: 1px solid #5a5e6d !important;
    border-radius: 0px!important;
    box-shadow: none!important;
}
#betslip-header {
    border-radius: 0px!important;
    background: #262626!important;
}

#betslip-header #betslip-title, #betslip-header .betslip-header-icon {
    color: #fff!important;
}
#betslip-text {
    color: #dcdcdc!important;
}
.betslip-error {
    border-radius: 0px!important;     
}
#betslip-label {
    background: #ffb129!important;
    color: #262626!important;
}
.tiny-blue-button {
    background-color: #bc2d2c !important;
    border-top-color: #C13B3A !important;
    border-bottom-color:#A92928 !important;
}
#betslip-type-buttons {
    border-top: none!important;
}
.betslip-type-button {
    width: 33.33%!important;
    border-top: none!important;
    border-bottom: none!important;
    background-color: #666!important;
    color: #ffb129 !important;
}
.betslip-type-button-active {
    background-color: #bc2d2c !important;
    color: #fff!important;
}
.betslip-type-button-disabled {
    color: #dcdcdc!important;    
}

.betslip-row {
    background: #262626!important;
    border-bottom: 2px solid #5a5e6d!important;
}
.betslip-row:last-of-type {
    border-bottom: none!important;
}
.betslip-row-event-text {
    color: #dcdcdc!important;
}
.betslip-row-bet-text {
    color: #fff!important;
}
.betslip-row-textbox-label {
    color: #dcdcdc!important;
}
#betslip-parlay-area, #betslip-teaser-area {
    margin-top: 0!important;
    border-top: 2px solid #5a5e6d!important;
}
#parlay-info, #teaser-info {
    background: #262626!important;
    border-radius: 0!important;
}

/** Wager **/
.mywager-outer2 {
    padding: 0!important;
    background: #262626!important;
    border: 1px solid #5a5e6d !important;
    border-radius: 0!important;
    box-shadow: none!important;
}
.mywager-header2 {
    padding: 10px 15px;
    background: #262626!important;
    border-bottom: 1px solid #5a5e6d !important;
}
.mywager-table {
    border-top: none!important;
    background: #3D3D3D!important
}
.mywager-table-row-pending, .mywager-table-row-cancel {
    background: none!important;
}
.mywager-table-row-lose {
    background: #962423!important;
}
.mywager-table-row-win {
    background: #7cad72!important;
}
.mywager-table-header2, .mywager-parlay-row {
    padding: 0 10px;
    box-sizing: border-box;
    background: #262626!important;
    border-bottom: 1px solid #5a5e6d !important;
    
    text-shadow: none!important;
    color: #fff!important;
}
.mywager-table-row2 {
    padding: 0 10px;
    box-sizing: border-box;
}
.mywager-table-row2:last-of-type {
    border-bottom: none!important;
}
.popover-content {
    color: #000!important;
}
.mywager-parlay-row {
    font-size: 12px!important;
}
}

/* i really want this to be global */

body.normal, #page_header {background: #f9f9f9}

.project_progress li {background-color: #f9f9f9;
}

  a.page_bar__page
  {
    box-shadow: 0 0 0 0;
  }

  h2.breadcrumbs
  {
    background-color: #c6d3d6;
  }

  .navigation_v2__tab, .navigation_v2__tab:visited
  {
    background-color: #ddccec;
  }

  body.supports_subnavigation.with_subnavigation .tabs--subnavigation {
    margin-top: 0;
    padding-top: 0;
    top: 1px;
    margin-bottom: 32px !important;
    border: 0 solid ;
    background-color: #f3e8fe;
    border-bottom: 1px solid #000;
    width: 100%;
    height: 37px;
}

  .navigation_v2__spacer
  {
    background-color: #ddccec;
  }

  .navigation_v2__notification, .navigation_v2__notification__wrapper
  {
    background-color: #ddccec;
  }

  .navigation_v2__logo
  {
    background-color: #ddccec;
  }

.clicker_v2--advanced_search {
    background-image: none;
    background-color: #a06fa9 !important;
}

#advanced_search_link, #advanced_search_link:visited {
    background-color: #a06fa9 !important;
    color: #000 !important;
}


  .clicker_v2
  {
    background-color: #ddccec;
    border: 0 solid ;
    border-radius: 10px;
    box-shadow: 1px 1px 0 0;
  }

  .clicker_v2:hover
  {                   
    background-color: #d2d2d2; 
    border: 0 solid ;
    border-radius: 10px; 
  }

  .forum_post_listing .forum_post_header
  {
    border: 1px solid  #eeeaf3;
    border-top-left-radius: 15px;
    border-top-right-radius: 15px;
  }

  .forum_post_listing .forum_post_row
  {
    border: 1px solid #eeeaf3;
    border-top: none;
  }

  body.topics_index .pagination a, .page_links .pagination a, .page_buttons a
  {
    border: 1px solid #ddccec;
    background-color: #f6f6f6;
  }

  body.topics_index .pagination a:hover, .page_links .pagination a:hover, .page_buttons a:hover
  {
    background-color: #e7dfed;
  }

  body.supports_subnavigation.with_subnavigation .breadcrumbs__crumb a
  {
    color: #000;
    font-size: 18px;
  }

  .page_bar__current
  {
    border: 1px solid #ddccec;
    margin-right: 4px;
    background-color: #cab1eb;
    font-weight: bold;
  }


  table#forum_posts thead td, table.grid th, table.grid tr.header td
  {
    background-color: #c6d3d6;
  }

  .topics #topic_toolbar
  {
    background-color: #ddccec; color: #3d3d3d;
  }

  .topics #topic_toolbar a, .topics #topic_toolbar a:visited
  {
    color: #000;
  }

  #topic_toolbar .title_overlay a
  {
    color: #000 !important;
    font-weight: bold;
  }

  .box_title, .box_title--standalone
  {
    background-color: #d2d2d2;
    border: 1px solid #ddccec;
    border-top-left-radius: 15px;
    border-top-right-radius: 15px;
  }

  .box_contents
  {
    background-color: #f5f5f5;
    border: 1px solid #ddccec;
    border-bottom-left-radius: 15px;
    border-bottom-right-radius: 15px;
  }

  .box
  {
    box-shadow: 0px 01px 0 0 #ddccec;
  }

  .resizable_table__cell-header
  {
    background-color: #e7dfed;
  }

   body.supports_subnavigation.with_subnavigation .tabs--dark {
    background-color: #7ca8b2;
    border-bottom-color: #000;
}

  body.with_navigation_v2 #footer
  {
    background-color: #cab1eb;
  }

  body.with_navigation_v2 #footer a, body.with_navigation_v2 #footer a:visited
  {
    color: #000;
  }

  #footer
  {
    color: #000;
  }

  body.search .advanced_search__facets
  {
    background-color: #e7dfed;
  }

  .forum_post_drafts__post
  {
    box-shadow: 0px 0px 0 0 #000;
  }
.forum_post_body .forum_post_linked_photo img {
    border: 1px solid #ddccec;
}

.queued_project__image .photo_border {
    background-image: none;
    float: left;
    overflow: hidden;
    padding: 0px;
    background-image: none;
    border: 1px #ddccec solid;
    box-shadow: none;
    margin-bottom: 12px;
}

.media_square_200 .photo_border {
    overflow: hidden;
    width: 210px;
    height: auto;
    padding: 0;
    margin: 0 !important;
    background-image: none;
    border: 1px solid #ddccec;
    box-shadow: none;
    margin-bottom: 4px !important;
}

.avatar img {
    border: 1px solid #ddccec;
    border-radius: 5px;
    z-index: 1;
}

#topics table th:first-of-type, #topics table td:first-of-type, .forum_glance table th:first-of-type, .forum_glance table td:first-of-type {
    border-left: 1px solid #ddccec;
}

#topics table td, .forum_glance table td {
    border-top: 1px solid #ddccec;
    border-bottom: none;
}

#topics table tr:last-of-type td, .forum_glance table tr:last-of-type td {
    border-bottom: 1px solid #ddccec;
}

#topics table th:last-of-type, #topics table td:last-of-type, .forum_glance table th:last-of-type, .forum_glance table td:last-of-type {
    border-right: 1px solid #ddccec;
}

#topics table th, #topics table tr:first-of-type td, .forum_glance table th, .forum_glance table tr:first-of-type td {
    border-top: 1px solid #ddccec;
}

#topics table, .forum_glance table {
    width: 100%;
    border-collapse: separate;
    border-spacing: 0;
    border-bottom: 1px solid #ddccec;
    background-color: transparent;
}

a.login {
    color: #000;
    font-size: 100%;
    font-weight: bold;
}

.tabs--subnavigation.tabs--dark .c-navigation_indicator__snake {
    border-bottom-color: #767676;
}

.tabs--subnavigation li .c-navigation_indicator__snake {
    border-bottom-width: 4px;
    border-color: #68999f;
}
.c-navigation_indicator__snake {
    border-bottom: 5px solid #477a85;
    width: 0px;
    transition: width 0s;
}

.c-navigation_dropdown--notebook .c-navigation_dropdown__content .c-navigation_dropdown__name {
    color: #888888;
    opacity: 0.8;
}

.c-navigation_dropdown--notebook .c-navigation_dropdown__content {
    background-color: #f9f9f9;
}

.c-navigation_dropdown--notebook .c-navigation_dropdown__content a:hover {background-color: #effaf9}

ul.tag_set li:not(.tag_set__control), span.tag_word {
    display: block;
    float: left;
    border-width: 1px;
    padding-bottom: 2px;
    margin-right: 7px;
    margin-bottom: 8px;
    overflow: hidden;
    padding-top: 1px;
    padding-right: 8px;
    padding-left: 8px;
    border-radius: 13px;
    border-color: #000;
    background-color: #ddccec;
}

.topics #topic_toolbar {
    background-color: #c6d3d6;
}

.links a, .topics #topic_toolbar .buttons a {
    color: #333 !important;
}

.topics #topic_toolbar .links a, .topics #topic_toolbar .buttons a {
    color: #333 !important;
}

