.svc_info,
.b-pagelist .btn,
.b-pagelist button,
.pgs-seaslist .tabs-title li,
.pgs-seaslist .tabs-title li:hover,
.tabs-title,
.header_txt,
.btn,
.btn_grp,
.pgs-back,
.pgs-back:before,
.poster-top:after,
.b-taglist.dl a,
.pgs-msg.error,
.pgs-head,
.tabs-title li.act,
.tabs-title li:hover,
.news-head,
.news-head:before,
.rside-title li.act,
