    text-shadow: 0px 0px 0px #fff0;
}
.tabs-result,
.tabs-title,
.rside-title {
    border: 0px solid #2f2b2c;
}
.svc_info:after,
.svc_info:before {
    border-right-color: #g5a5c62;
}
.pgs-back:after,
.pgs-head:after,
.pgs-head:before,
.pgs-afterplay,
.news a:nth-child(odd) {
    background: #000;
    border-right-color: #j5a5c62;
}
.header_icon a:last-child,
.header_icon span:last-child {
    background: #y5a5c62;
}
.pgs-sinfo-actor-title:after,
.pgs-sinfo-actor-title:before {
    border-top: 2px solid #ac989f;
}
.lside-in.letter,
.rside-result,
.sidebar.rside,
.lside-block {
    background: black;
    border: 0px solid black;
}
.news [class*=" svico-"],
.news [class^=svico-] {
    color: #fff;
}
.lside-serial a:nth-child(2n) {
    background-image: linear-gradient(0deg, #h5a5c62 0, #h5a5c62);
}
.lside-blockg .btn_grp input[type=checkbox]:checked + span,
.lside-blockg .btn_grp input[type=radio]:checked + span {
    color: #fff;
}
.lside-blockg .btn_grp input[type=checkbox] + span,
.lside-blockg .btn_grp input[type=radio] + span {
    opacity: 1;
}
.tabs-title li {
