@-moz-document regexp("(http|https)://(www.|)thepiratebay.*") {
/* ------------ Main ------------- */
body {
    background-color: #000000 !important;
    color: #CCC !important;
}

#fp h1 a,
img[alt="The Pirate Bay"],
nav section a img[src$="tpb.jpg"],
img[alt="RSS"] {
    mix-blend-mode: screen;
    filter: invert(100%);
}


a {
    color: #c1c1c1 !important;
    border-bottom: none !important;
    transition: 500ms;
}

a:hover {
    color: #efffff !important;
    border-color: #80FF00 !important;
    transition: 500ms;
    text-decoration: underline;
}

input[type="search"] {
    background-color: #202020 !important;
    border: 1px solid #303030 !important;
    color: #CCC !important;
}

input[type="text"] {
    background-color: #202020 !important;
    border: 1px solid #3c5c6f !important;
    color: #CCC !important;
}

input[type="submit"] {
    background-color: #202020 !important;
    border: 2px solid #666666;
    color: #CCC !important;
    border-radius: 3px;
    transition: 500ms;
    border-style: outset;
}

input[type="submit"]:hover {
    background-color: #444444 !important;
    border-color: #444444 !important;
    color: #ffffff !important;
    cursor: pointer !important;
    transition: 500ms;
}


h1, h2 {
    background-color: #202020 !important;
    border-color: #303030 !important;
}

select,
select[size="0"],
select[size="1"] {
    border-color: #303030 !important;
}

select {
    background-color: #202020 !important;
    border-radius: 3px;
    color: #CCC !important;
}

select optgroup {
    color: #6299b8 !important;
}

body::before {
    position: fixed !important;
    color: #202020 !important;
    font-size: 7pt !important;
    font-family: "Segoe UI", Arial, Helvetica, sans-serif !important;
    content: "Simple Dark TPB" !important;
    bottom: 10px !important;
    left: 4px !important;
    z-index: 1050 !important;
}

body::after {
    position: fixed;
    color: #202020;
    font-size: 7pt;
    font-family: "Segoe UI", Arial, Helvetica, sans-serif;
    content: "Custom Theme";
    bottom: 0px;
    left: 4px;
    z-index: 1050;
}

/* ------------ Browse - v1 (tables) ----------- */
.detDesc {
    color: #AAA !important;
}

table#searchResult td.vertTh {
    color: #CCC !important;
}

thead#tableHead th {
    background-color: #303030 !important;
}

table#searchResult th,
table#searchResult td {
    border-color: #303030 !important;
}

table#searchResult tr:nth-child(odd) {
    background-color: #202020 !important;
}

table#searchResult tr:nth-child(even) {
    background-color: #252525 !important;
}

table#searchResult tr:hover {
    background-color: #303030 !important;
}

/* ------------ Browse - v2 (list) ----------- */
ol#torrents td.vertTh {
    color: #CCC !important;
}

ol#torrents .list-header {
    background-color: #303030 !important;
}

ol#torrents .list-header {
    border-color: #303030 !important;
}

ol#torrents li:nth-child(odd) {
    background-color: #202020 !important;
}

ol#torrents li:nth-child(even) {
    background-color: #252525 !important;
}

ol#torrents li:hover {
    background-color: #303030 !important;
}

/* ------- Torrent Details (v1) ------- */
#detailsframe,
#detailsframe #title {
    background-color: #303030 !important;
    border-color: #303030 !important;
}

#details {
    background-color: #202020 !important;
}

#details dd {
    border-color: #303030 !important;
}

#details dt {
    border-color: #303030 !important;
    color: #AAA !important;
}

.nfo,
#filelistContainer {
    background-color: #303030 !important;
    border-color: #404040 !important;
    font-family: Consolas, Monaco, "Ubuntu Mono", monospace !important;
    line-height: 1.15 !important;
    padding-bottom: 5px !important;
}

#details h4,
#comments p.byline {
    color: #AAA !important;
}

#comments .comment {
    background-color: #151515 !important;
}

/* ------- Torrent Details (v2) ------- */
div#description_container {
    background-color: #252525 !important;
}

div#description_container div#metadata *{
    border-color: #606060 !important;
}

div#description_container div#metadata dl#meta-left div:hover {
    background-color: #171717 !important;
}

div#description_text,
div#filelist,
div#comments {
    background-color: #303030 !important;
    border-color: #505050 !important;
}

div#filelist ol li:nth-child(odd) {
    background-color: #303030 !important;
}

div#filelist ol li:nth-child(even) {
    background-color: #353535 !important;
}

div#filelist ol li:hover {
    background-color: #171717 !important;
}

/* ------------- Blog ------------ */
#blog #comments dt {
    background-color: #050505 !important;
    color: #AAA !important;
}

/* ------ RSS Subscriptions ------ */
#subscribe li a:hover {
    background-color: #202020 !important;
}
}

