@namespace url(http://www.w3.org/1999/xhtml);
body,html {
	background-color: #000000;
	color:white;
	font-size:14px;
}
DIV[style="background-color:white"] {
	background-color: #000000 !important;
}

a {
	color:#AAAAAA;
	text-decoration:none;
}
a:visited {
	color:#999999;
	text-decoration:none;
}
a:hover {
	text-decoration:underline;
}
/* but prevent stupid non-closing <a> spanning whole pre*/
a[name="alku"] {
	color:white;
	text-decoration:none;
}
a[name="alku"]:hover {
	color:white;
	text-decoration:none;
}
/* some color substitutions to match teletxt */
