	color:white;
}
font[color="#0080FF"] {
	color:#04FEFC;
}
font[color="#008000"] {
	color:#04FE04;
}
font[color="#FF0080"] {
	color:#FCFE04;
}
font[color="#0000FF"] {
	color:#0402FF;
}
/* "beef" - preformatted area */
pre a {
	color:inherit;
	text-decoration:none;
}
pre a:visited {
	color:inherit;
	text-decoration:none;
}
pre a:hover {
	text-decoration:underline;
}
pre {
	font-family: "Lucida Console" ,monospace;
	font-size:16px;
	font-weight:500;
    background-color: #000000;
}
