#page-manager.ytd-app {margin-top: calc(var(--ytd-masthead-height, var(--ytd-toolbar-height)) - 72px);}
ytd-browse[page-subtype~=channels] ytd-two-column-browse-results-renderer.ytd-browse {margin-top: 72px;}
ytd-app[guide-persistent-and-visible] div ytd-page-manager ytd-browse div ytd-c4-tabbed-header-renderer app-header-layout div app-header div #channel-container, #contentContainer.app-header {margin-top: 15px;}
#wrapper.app-header-layout > [slot=header] {top: calc(var(--layout-fixed-top_-_top) - 0px);}
ytd-app[mini-guide-visible_] ytd-page-manager.ytd-app {margin-left: 52px;}
ytd-app[guide-persistent-and-visible] ytd-page-manager.ytd-app {margin-left: 220px;}
app-toolbar.ytd-c4-tabbed-header-renderer {transform: none !important;}

#links-holder.ytd-c4-tabbed-header-renderer {
 border-radius: 20px;
 overflow: hidden; 
 backdrop-filter: blur(5px); 
 background-color: var(--by-main-fg-color-tp);
}
#content-attachment.ytd-backstage-post-renderer ytd-video-renderer.ytd-backstage-post-renderer, #content-attachment.ytd-backstage-post-renderer ytd-playlist-renderer.ytd-backstage-post-renderer {
 border-radius: 25px;
 background-color: var(--by-main-bg-color);
}
#bg.ytd-interactive-tabbed-header-renderer {fill: var(--by-main-bg-color); margin-top: -20px;}
}
