@-moz-document domain("lemonde.fr") {
#Header .Header,
.Nav,
.main,
.zone {
    border-bottom: .1rem solid rgb(98, 98, 98);
    background-color: rgb(38, 38, 38) !important;
    color: rgb(200, 200, 200);
}

a {
    color: #57bfe6 !important;
}

img,
vid {
    filter: brightness(0.6) !important;
    border: none !important;
}

.article__title,
.article--main .article__desc,
.article--main .article__desc,
.article--runner .article__desc,
.article--related .article__title,
.area--section .area__title,
.article--opinion .article__title,
.article__author-name,
.article__desc,
.article__paragraph,
.article--featured .article__type,
.article--opinion .article__type,
.article--single .article__author-identity,
.article--single .article__inner,
.teaser__title,
.teaser__desc,
.article__ordered-list li h2,
.article__sub-title,
.article__unordered-list li h2,
.podcast__main-title,
.podcast__main-desc,
.paywall {
    color: rgb(200, 200, 200);
}

.article--featured,
.article--featured .article__wrapper,
.article--longform,
.article--longform .article__heading,
.footer,
.paywall,
.paywall__container,
.paywall__header {
    background: rgb(38, 38, 38) !important;
}

.en-continu,
.en-continu .Live--extended,
.en-continu .Live--extended .New a {
    background-color: #013548 !important;
}

#Header .right .btn--premium,
.btn--premium:hover,
.message--engagement.message--shown,
.button--subscribe {
    background-color: #ce3701;
    border: #ce3701;
}

.wforecast_tops_0,
.wforecast_tops_0-articles,
.wforecast_tops_0-articles-type,
.wforecast_tops_0-article-title,
.insert {
    background-color: rgb(55, 55, 55);
    color: rgb(200, 200, 200);
}

.Nav__sub-item {
    background-color: rgb(55, 55, 55);
}
.Nav__item--active:nth-child(n+4):not(:last-child) {
    background-color: rgb(55, 55, 55);
}

.live,
.tab__content--fact,
.tab__content--live,
.form__container-live--overlay {
    background: rgb(38, 38, 38);
    color: rgb(200, 200, 200);
}

.tab__nav .tab1--fact,
.tab__nav .tab2--second-title {
    border-bottom: .3rem solid rgb(200, 200, 200);
    color: rgb(200, 200, 200);
}

.article__ordered-list li,
.article__unordered-list li {
    color: rgb(200, 200, 200);
}

.d_element,
.d_element_bottom,
.d_encadre {
    border: 1px solid rgb(98, 98, 98);
    background: rgb(38, 38, 38);
}

.d_principal {
    color: rgb(200, 200, 200);
}

.area--section {
    border-top: .2rem solid rgb(200, 200, 200);
}

.wforecast_tops_0-article-number {
    color: rgb(200, 200, 200);
}

.article--single .article__inner::after {
    background-color: rgb(200, 200, 200);
}

.zone--homepage,
.zone--article {
    box-shadow: 0 .2rem rgb(55, 55, 55) inset, .15rem 0 rgb(55, 55, 55) inset, -.15rem 0 rgb(55, 55, 55) inset;
}

.page__actions-container::after {
    background-image: linear-gradient(to right, rgba(38, 38, 38, .65), rgba(38, 38, 38));
}
.paywall__container::before {
    background-image: linear-gradient(rgba(38, 38, 38, .65), rgba(38, 38, 38));
    width: 100%;
}

.content--live {
	color: rgb(200, 200, 200);
}

}

