@-moz-document regexp("https:\\/\\/.*hattrick.org\\/?([a-z\\-]+\\/)?World\\/Series\\/?\\?LeagueLevelUnitID=([0-9]*)") {
#mainBody {
    font-family: arial, sans-serif;
}

h1.hasByline, #ctl00_ctl00_CPContent_CPMain_UpdatePanelLiveLeagueTable {
    color: rgba(0, 0, 0, .87);
}

#ctl00_ctl00_CPContent_CPMain_UpdatePanelLiveLeagueTable > span:first-child{
    display: block;
    margin: 10px;
}

table.series-table, #ctl00_ctl00_CPContent_CPMain_UpdatePanelLiveLeagueTable {
    border-collapse: separate;
    font-size: 14px;
}

table.series-table tr > th {
    background: transparent;
    color: rgba(0, 0, 0, .54);
    font-weight: normal;
    border-bottom: solid 1px #F1F3F4;
    height: 17px;
    font-size: 12px;
}

table.series-table tr > td:first-child {
    border-left: solid 3px transparent;
    padding-left: 12px;
}

table.series-table tr:nth-child(2) > td:first-child {
    border-left-color: #4285F4;
}

table.series-table tr:nth-child(6) > td:first-child, table.series-table tr:nth-child(7) > td:first-child {
    border-left-color: #FBBC04;
}

table.series-table tr:nth-child(8) > td:first-child, table.series-table tr:nth-child(9) > td:first-child {
    border-left-color: #EA4335;
}

table.series-table tr > td {
    border-bottom: none;
    height: 37px;
    font-weight: normal;
}

table.series-table tr > th:nth-child(2), table.series-table tr > td:nth-child(2) {
    display: none;
}

table.series-table .series-table-logo img {
    max-height: 30px;
    max-width: 24px;
}

.recent-result {
    width: 16px;
    height: 16px;
    background-color: transparent;
    border: none;
    margin: 0 2px;
}

.recent-result i {
    display: none;
}

.recent-result-won {
    background-image: url(data:image/svg+xml;base64,);
}

.recent-result-draw {
    background-image: url(data:image/svg+xml;base64,);
}

.recent-result-lost {
    background-image: url(data:image/svg+xml;base64,);
}

table.left tr > th, table.right tr > th {
    font-weight: normal;
    background: transparent;
    border: none;
    margin-bottom: 10px;
}
}

