    border: 1px solid #0000FF;
      /*Search border color*/
    ;
  }

  body
  {
    background: transparent !important;
  }

  .content
  {
    background: transparent !important;
  }

  #fb-root
  {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url(https://images.designtrends.com/wp-content/uploads/2016/09/18154613/Black-and-Blue-Background.jpg) !important;
      /* Background */
    background-size: cover !important;
    background-attachment: fixed !important;
  }

  #party_none_title, div.friend_dock_chatbox_titlebar.chat-header-blink-off
  {
    background: linear-gradient(to bottom, #0000FF, #000000);
  }

