    width: auto;
    margin-right: 10px !important;
    margin-left: 10px !important;
  }

  #GamesListsContainer > div
  {
    background-color: transparent !important;
    width: 100% !important;
  }

  #navigation
  {
    background-color: #0000FF;
  }

  .notification-red
  {
    background-color: #000000;
    padding: 1px 6px;
    min-width: 18px;
  }

  .notification-blue
  {
    background-color: #0000FF;
    padding: 1px 6px;
    min-width: 18px;
  }

  .rbx-btn-primary-lg
  {
    background-color: rgba(200, 160, 248, 0.5);
  }

  .rbx-btn-primary-lg:hover, .rbx-btn-primary-lg:focus
  {
    background-color: rgba(200, 160, 248, 0.5);
  }

  .avatar-back
  {
    background-image: url(https://images.designtrends.com/wp-content/uploads/2016/09/18154613/Black-and-Blue-Background.jpg);
  }
}

/* i really want this to be global */

:root{--wfilter: hue-rotate(180deg) invert(1)}
html:fullscreen{ --wfilter: none}

html {background-color: white; -webkit-filter: var(--wfilter) !important}

iframe:not(:fullscreen),
img,
video,
embed[flashvars],
div[style*=".jpg"],
div[style*=".jpeg"],
span[style*=".jpg"],
*[type*="application/x-shockwave-flash"],
*[type*="application/x-silverlight"],
div[class*="html5-video-player unstarted-mode"]    
{-webkit-filter: var(--wfilter) !important}

img[class^="css-"]{opacity: unset !important}

