    a.help_tooltip:hover {
        color: whitesmoke;
    }
    .alert-info {
        background-color: #333;
    }
    .well .box_head {
        position: sticky;
        top: 165px;
    }
    #tabMenu {
        position: sticky;
        top: 197px;
