        z-index: -1;
    }
    #TopBanner #syslog_panel_button:hover .label-important::after {
        box-shadow: 0 0 2px 2px /*[[alert-color]]*/;
        filter: drop-shadow(0 0 3px /*[[alert-color]]*/) drop-shadow(0 0 5px /*[[alert-color]]*/);
    }
    #Loading,
    #LoadingBar {
        bottom: 0;
        left: 0;
        position: fixed !important;
        right: 0;
        top: 0;
    }
}

