data.css:
@-moz-document domain("lichess.org"), domain("lichess.dev") {
.user-link:before {
    margin-right: 3px;
}
.username .text:before,
.user-link:before {
    content: 'LM' !important;
    color: #CC9000;
    text-shadow: none !important;
    position: relative;
}
div.username:before {
    display: none;
}
.username .text:before {
    content: "LM " !important;
}
.user-link .title {
    display: none;
}
#friend_box div .user-link {
    padding: 7px;
}
.players .user-link:before,
.entry .user-link:before,
.undertable_inner .user-link:before,
.winner .user-link:before,
.messages .user-link:before,
.inline_userlist .user-link:before,
.player .user-link:before,
.user .user-link:before {
    position: static;
}
}

