@-moz-document domain("namu.wiki") {

.test-ad-card,
.ad-card,
.adsense,
.wiki-inner-content + iframe,
.ad-bottom,
.kakao_ad_area,
.adsbygoogle,
article > div:last-of-type iframe,
.ad,
div[id^="google_ads_iframe_"] {
  display: none !important;
}

/* content-wrapper */
.senkawa-fixed-1300 > div:nth-child(2) {
  margin-left: 1% !important;
  max-width: none !important;
  width: 76% !important;
}
/* wiki-article */
.senkawa-fixed-1300 > div:nth-child(2) > article {
  width: 100% !important;
  min-height: calc(100vh - 42px - 119.17px) !important;
}

.wiki-macro-footnote {
  position: fixed !important;
  bottom: 0 !important;
  background-color: #fff !important;
  max-height: 150px !important;
  width: 72.4% !important;
  overflow: auto !important;
  box-shadow: 0px 0px 3px !important;
  transition: .3s !important;
  z-index: 1 !important;
}

.wiki-macro-footnote:hover {
  max-height: 300px !important
}

/* side */
aside {
  position: fixed !important;
  right: 0 !important;
  top: 67px !important;
  display: flex !important;
  flex-direction: row !important;
  justify-content: flex-start !important;
  width: 22% !important;
  z-index: 1 !important;
  max-height: 80vh !important;
  overflow-y: auto !important;
  border: .0625rem solid #ccc !important;
  background: white !important;
}

aside > div {
  max-width: 50% !important;
  height: 100% !important;
  margin-bottom: 0 !important;
  border: none !important;
}

aside > div:nth-child(1) {
  border-right: .0625rem solid #ccc !important;
}

/* search box */
nav form > div {
  position: fixed !important;
  right: 15px !important;
  top: 42px !important;
  width: 22.05% !important;
  z-index: 2 !important;
}

/* left-search-btns */
nav form > div > span:nth-child(1) {
  width: 8% !important;
  vertical-align: top !important;
}

nav form > div > span:nth-child(1) a {
  width: 100% !important;
}
/* searchInput */
nav form > div > div:nth-child(2) {
  width: 84% !important;
}
/* right-search-btns */
nav form > div > span:nth-child(3) {
  float: right !important;
  width: 16% !important;
}

nav form > div > span:nth-child(3) button {
  float: right !important;
  margin-left: 0 !important;
  width: 50% !important;
}

nav form > div > span:nth-child(3) button:nth-child(2) {
  border-left: 0 !important;
}

.popper {
  z-index: 2 !important;
}

@media (max-width: 1110px) {
  .senkawa-fixed-1300 > div:nth-child(2) {
    margin-left: 0% !important;
    width: 100% !important;
  }

  .senkawa-fixed-1300 > div:nth-child(2) > article {
    margin-right: 0 !important;
  }

  .wiki-macro-footnote {
    max-height: 10px !important;
    width: 90% !important;
  }

  .wiki-macro-footnote:hover {
    max-height: 150px !important
  }

  nav form > div {
    right: 20px !important;
    top: 73px !important;
    width: 30.05% !important;
    background-color: white !important;
  }

  nav form > div > div:nth-child(2) {
    width: 75% !important;
  }

  nav form > div > span:nth-child(3) {
    width: 25% !important;
  }

  nav form > div > span:nth-child(1) a,
  nav form > div > div:nth-child(2),
  nav form > div > span:nth-child(3) button {
    background-color: rgba(255, 255, 255, 0.3) !important;
  }

  nav form > div > span:nth-child(3) button:nth-child(2) {
    border-right: 1px solid #ccc !important;
  }

  aside {
    top: 91px !important;
    right: 4px !important;
    max-height: 100px !important;
    border: 1px solid #ccc !important;
    width: 30% !important;
    background-color: white !important;
    transition: .3s !important;
    font-size: 0.8rem !important;
  }

  aside:hover {
    max-height: 200px !important
  }

  aside > div {
    margin-bottom: 0 !important;
  }
  
  aside > div:nth-child(1) {
    border-right: 0 !important;
  }

  aside h5 {
    font-size: 0.8rem !important;
    margin: 0 !important;
    padding: 20px !important;
    text-align: center !important;
  }

  aside > div:nth-child(1) h5 {
    border-right: 1px solid #ccc !important;
  }

  aside > div:nth-child(1) a {
    overflow: hidden !important;
    border-right: 1px solid #ccc !important;
  }
}

}

@-moz-document domain("www.baidu.com"), domain("www1.baidu.com") {
body {
    overflow-x: hidden;
    min-width: 600px;
    font-family: "微软雅黑";
}
.wrapper_new #content_left {
    padding-left: 20px;
}
.wrapper_l .s_form {
    width: 792px !important;
    margin: 0 auto !important;
    transform: translatex(-40px);
}
.s_tab,
.nums,
.search_tool_conter,
#rs,
.to_tieba,
.to_zhidao_bottom {
    margin: 0 auto !important;
}
.headBlock {
    margin-left: 0;
}
.headBlock > div {
    margin: auto;
}
.head_nums_cont_inner > div {
    width: 600px;
}
#page,
#s_tab {
    padding-left: 0px !important;
    text-align: center !important;
}
#container,
#container.sam_newgrid {
    width: 100%;
    margin-left: 0;
    padding-left: 20px;
}
#content_right {
    display: none;
}
#container.sam_newgrid #content_left {
    width: calc(100% - 40px);
}
#content_left {
    padding: 0;
    width: calc(100% - 40px);
    padding-left: 2%;
    display: block;
    counter-reset: results;
    column-gap: 0;
    -moz-column-gap: 0;
    -webkit-column-gap: 0;
}
.rrecom-container,
.xpath-log:not(.c-container) {
    transform: scale(0);
}
@media (min-width: 1201px) and (max-width: 1700px) {
    #content_left {
        column-count: 2;
        -moz-column-count: 2;
        -webkit-column-count: 2;
    }
}
@media (min-width: 1701px) and (max-width: 2200px) {
    #content_left {
        column-count: 3;
        -moz-column-count: 3;
        -webkit-column-count: 3;
    }
}
@media (min-width: 2201px) {
    #content_left {
        column-count: 4;
        -moz-column-count: 4;
        -webkit-column-count: 4;
    }
}
#content_left > *:not([class^="result"]) {
    position: absolute;
    margin-top: -1em;
    transform: scale(0);
}
#content_left > *[class^="result"] {
    display: inline-block;
    width: 91%;
    padding: 18px 15px;
    margin: 1em;
    word-break: normal;
    word-wrap: normal;
    position: relative;
    z-index: 1;
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
    border-radius: 2px;
    background-color: white;
    overflow: hidden;
}
#content_left > *[class^="result"]:before {
    counter-increment: results;
    content: counter(results);
    z-index: -10;
    color: #eee;
    font-size: 4.5em;
    position: absolute;
    text-align: right;
    width: 96%;
    line-height: 1em;
    top: 6px;
}
#content_left > *[class^="result"] .sitelink {
    word-wrap: break-word;
}
#content_left .result-op > div {
    width: 100%;
    box-sizing: border-box;
}
.c-border {
    border: 0;
    box-shadow: none;
}
.to_tieba {
    display: none;
}
.hint_common_restop {
    display: none;
}
#rs {
    width: 100%;
    text-align: center;
    padding: 0;
    margin: 0;
    margin-top: 10px;
}
#rs .tt,
#rs table,
#rs tr,
#rs td,
#rs th {
    display: inline;
    padding: 0 5px;
}
#page {
    margin: 15px 0;
    text-align: center;
    padding: 0;
}
#page .fk {
    display: none;
}
#page > a,
#page > strong {
    height: auto;
}
#foot {
    margin-top: 35px;
    text-align: center;
}
#foot #help {
    float: none !important;
    padding: 0 !important;
}
.c-span6 {
    width: 25% !important;
    margin-right: 0 !important;
    padding-left: 3px;
    padding-right: 3px;
    box-sizing: border-box;
}
.c-span18 {
    width: 75% !important;
    padding-left: 10px;
    box-sizing: border-box;
}
/* 百度图片 */
.op-img-address-divide-low {
    height: auto;
    display: flex;
}
.op-img-address-link-type {
    float: none;
}
/* 百度经验 */
.c-span6.op_jingyan_list {
    margin-right: 0;
}
.op_jingyan_list > a {
    display: block;
    width: 100%;
}
.op_jingyan_list > a .c-img6 {
    width: 100%;
    height: auto;
}
.op_jingyan_list .op_jingyan_index {
    left: 5px;
    top: 67px;
}
/* 百度贴吧 */
.op-tieba-general-main-col {
    width: 70% !important;
}

.sitelink_summary {
    width: 46%;
}
#wrapper_wrapper {
    margin: 0 !important;
}
}

