@-moz-document regexp("https?://www.baidu.com/?") {
/*代码1，首页界面设置*/
/*预设开始*/
html{
/*     隐藏 竖滚动条 */
    overflow-y: hidden;
}
body {
    background-image:url(http://api.btstu.cn/sjbz/?lx=dongman) !important;
    background-repeat:no-repeat !important;
    background-position: top center !important;
    background-size: cover!important;
    background-attachment: fixed !important;
    font-family:"Microsoft YaHei" !important;
}
html,
body{
    height: 100%;
}
a[target="_self"]{
    display:none!important;/*去除显示卡片*/
}
/*预设结束*/

/* 顶部栏开始 */
#s_top_wrap/*顶栏背景*/{
    background-color: rgba(255, 255, 255, .8) !important;
}
/* 顶部栏结束 */

/*调整搜索框位置*/
.s_form{
    top: 240px !important;
    bottom: auto !important;
}
.s_form .fm{
    top: 0 !important;
}
#lg{
    /*display: none !important;*/
    visibility: hidden !important;
}
/* 设置面板位置调整*/
#wrapper>div:last-child{
    position:absolute;
    top:0px!important;
}
/*天气面板*/
.weather-setting-content{
    background-color: rgba(255,255,255,0.95) !important;
}
/* 我的关注、推荐、导航 */
#s_main {
    border:3px outset rgba(200,200,200,.8);
    background-color:rgba(255,255,255,.5);
}
/*屏蔽多余元素*/
.s-hotsearch-wrapper,/*首页推荐*/
.s-treasure,/*↓底部信息↓*/
#ftCon,
#s_lm_wrap,
#lm-new,
.qrcodeCon,
.no-qrcode-layer,#bottom_layer{
    display: none !important;
}
/*更多产品*/
.bdbriscroll-onwheel{
    height: auto !important;
}
}

@-moz-document regexp("https?://www.baidu.com/s(\\?)(?!(.*)(=news&))(.*)") {
/*代码2，网页界面设置*/
/*预设开始*/
body {
    background-image: url(http://static.oneplus.cn/data/attachment/forum/201909/15/151337y2z3h3sxm5sx32f8.jpg) !important;
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
    background-position: center top !important;
    background-size:cover!important;
    font-family:"Microsoft YaHei" !important;
}
#wrapper
{
    min-width:1200px;
}
/*预设结束*/


/*头部设置开始*/
#s_tab {
    background-color:rgba(255,255,255,.95)!important;
    height: 45px!important;
}
.s_form {
    height: 40px;
    margin:0!important;
}
.s_tab_inner
{
    position:relative;
    top:10px;
}
.head_nums_cont_outer
{
    position:absolute;
    width:435px;
    top:-38px;
    left:70%;
}
.search_tool_conter
,.nums
{
    margin:0!important;
    width:438px;
}
.search_tool
,.search_tool_close
{
    position:relative;
    right:10px;
}
/*头部设置结束*/


/*左侧面板设置开始*/
.hint_common_restop,
.content_none/*没有找到*/
{
    margin-left:140px;
}
.result-op .t+div>canvas/*时间*/
{
    width:640px!important;
}
.hint_common_restop,
#rs_top_new,/*相关搜索*/
#content_left .result,
#content_left .result-op,
#rs,
#op_wiseapp_td,
.ec-pl-container,
.hit_top_new,
#content_left div:first-child[class$="_rs"]/*相关推荐*/
,.content_none
,.EC_ppim_new_gap_bottom/*广告*/
{
    background-color: rgba(255, 255, 255, .8);
    width: 640px!important;
    padding: 15px;
    border-radius: 15px;
    box-shadow: rgba(0, 0, 0, 0.3) 1px 1px 5px 1px;
    transition: background-color 1s;
}
.aladdin__2AELi
,.wenda-abstract-wrap{
    border:none;
    box-shadow:none;
    background-color: rgba(255, 255, 255, 0.05);
}
#op_wiseapp_td
{
    padding:15px!important;
}

#content_left .result:hover,
#content_left .result-op:hover,
#rs:hover,
#op_wiseapp_td:hover,
.hit_top_new:hover,
.ec-pl-container:hover,
#content_left div:first-child[class$="_rs"]:hover,
.content_none:hover
,.EC_ppim_new_gap_bottom:hover
{
    background-color: rgba(255,255,255,0.95);
}
/*左侧面板设置结束*/


/*右侧面板设置开始*/
#container
{
    width:90%;
}
#content_right{
    position:relative;
    border-style: none;
    display:block;/*隐藏右侧面板填none*/
