}

/* Place The scroll up button at the up */
::-webkit-scrollbar-button:vertical:decrement {
    background-color: #4d4d4d;
    border: 2.5px solid #525253;
             
}

::-webkit-scrollbar-button:vertical:increment {
    background-color: #4d4d4d;
    border: 2.5px solid #525253;
}


/* Top area above thumb and below up button PUNTITOS DE LA BARRA VERTICAL */
::-webkit-scrollbar-track-piece:vertical:start {
    border: 1px solid #2a2a2a; /* #373738   */
}
 
/* Bottom area below thumb and down button PUNTITOS DE LA BARRA VERTICAL */
::-webkit-scrollbar-track-piece:vertical:end {
    border: 1px dashed #505050;
}

/* Top area above thumb and below up button PUNTITOS DE LA BARRA HORIZONTAL */
::-webkit-scrollbar-track-piece:horizontal:start {
    border: 1px solid #2a2a2a; /* #373738   */
}
 
/* Bottom area below thumb and down button PUNTITOS DE LA BARRA HORIZONTAL*/
