/* La LETRA RESALTADA según el Tema (Theme) */
.theme_todoist   .text > code {background-color: #EAC800;    color: #000000; font-weight: bold; font-size: 16.3px;}
.theme_noir      .text > code {background-color: #A9A9A9;    color: #000000; font-weight: bold;   font-size: 16.3px;}
.theme_dark      .text > code {background-color: #d0ae00e8;    color: #fffb00e8; font-weight: bold; font-size: 21px;font-family:"Verdana";}
.theme_neutral   .text > code {background-color: #252525;    color: #10842F; font-weight: normal; font-size: 21.5px;font-family:"Verdana";}
.theme_tangerine .text > code {background-color: #d78a0c;    color: #495b60; font-weight: normal; font-size: 16.3px;}
.theme_sunflower .text > code {background-color: #d01e1e;    color: #a8d021; font-weight: normal; font-size: 16.3px;}
.theme_clover    .text > code {background-color: #22893a;    color: #c3c4bf; font-weight: normal; font-size: 16.3px;}
.theme_blueberry .text > code {background-color: #296bc4;    color: #cccec5; font-weight: normal; font-size: 16.3px;}
.theme_sky       .text > code {background-color: #0059fe;    color: #A0CF00; font-weight: normal; font-size: 16.3px;}
.theme_amethyst  .text > code {background-color: #444444;    color: #A0CF00; font-weight: normal; font-size: 16.3px;}
.theme_graphite  .text > code {background-color: #EAC800;    color: #000000; font-weight: normal; font-size: 16.3px;}


.theme_todoist  .ital > code {background-color: #EAC800;    color: #000000; font-weight: normal; font-size: 16.3px;}
