@-moz-document domain("promobit.com.br") {
  /* General */
  :root {
    --promobit-yellow: #faba01;
    --promobit-yellow-dimmed: #faba014a;
    --background-color: #303030;
    --subheader-color: #424242;
    --divider-color: rgba(255, 255, 255, 0.12);
    --text-primary-color: #ffffff;
    --text-secondary-color: rgba(255, 255, 255, 0.7);
    --text-tertiary-color: rgba(255, 255, 255, 0.5);
    --icon-color: #ffffff;
    --search-input-color: rgba(255, 255, 255, 0.09);
    --search-input-focus-color: #ffffff;
    --search-input-text-color: #4c4c4c;
    --header-tab-color: var(--search-input-color);
    --header-tab-active-color: #424242;
    --subheader-button-transparent-color: var(--search-input-color);
    --general-button-color: var(--search-input-color);
    --sidebar-color: var(--subheader-color);
    --timeline-card-color: var(--subheader-color);
    --timeline-card-title-color: #565656;
    --offer-card-color: var(--subheader-color);
    --offer-card-bottom-color: var(--divider-color);
    --forum-thread-container-color: #4f4f4f;
    --forum-fixed-thread-color: var(--offer-card-color);
  }

  body {
    background: var(--background-color);
  }

  .in-alert {
    background: linear-gradient(90deg, #faba01, #b78a07);
    background-size: 400% 400%;
    animation: MovingAlertBg 3s ease infinite;
  }

  .banner-cta {
    box-shadow: none;
  }

  /* Header */
  .pr-top {
    background: var(--background-color);
  }

  .pr-top .pr-menu-tabs .pr-tab-item,
  .pr-top .pr-menu-tabs .pr-tab-item:hover {
    background-color: var(--header-tab-color);
  }

  .pr-top .pr-menu-tabs .pr-tab-item:hover {
    box-shadow: 0 -2px 1px var(--promobit-yellow);
  }

  .pr-top .pr-menu-tabs .pr-tab-item .pr-label__container {
    color: var(--text-secondary-color);
  }

  .pr-top .pr-menu-tabs .pr-tab-item.active {
    background-color: var(--header-tab-active-color);
    border: 1px solid var(--divider-color);
    border-bottom: none;
    box-shadow: 0 -2px 1px var(--promobit-yellow);
  }

  /* "Term" nao tem H :p */
  .pr-top .pr-pagemenu-search .search_therm {
    background-color: var(--search-input-color);
    color: var(--text-secondary-color);
  }

  .pr-top .pr-pagemenu-search .search_therm.focus {
    background-color: var(--search-input-focus-color);
    color: var(--search-input-text-color);
  }

  .pr-top .pr-pagemenu-search .search_submit {
    background-color: transparent;
    border: 1px solid var(--divider-color);
  }

  .pr-top .pr-pagemenu-search .search_submit:hover {
    background-color: var(--divider-color);
  }

  .pr-pagemenu {
    background: var(--subheader-color);
    border-bottom-color: var(--divider-color);
  }

  .pr-pagemenu .pr-submenu-item {
    color: var(--text-secondary-color);
  }

  .pr-pagemenu .pr-submenu-item:hover {
    font-weight: bold;
    color: var(--text-tertiary-color);
  }

  .pr-dd-container {
    background: var(--subheader-color);
    border-color: var(--divider-color);
  }

  .pr-pagemenu .pr-submenu-item .pr-dd-container .pr-submenu-categories .cat-item {
    border-bottom-color: var(--divider-color);
  }

  .pr-pagemenu .pr-submenu-item .pr-dd-container .pr-submenu-categories .cat-item a,
  .pr-pagemenu .pr-submenu-item .pr-dd-container .pr-submenu-categories .cat-item h4 {
    color: var(--text-primary-color);
  }

  .pagemenu__btn--transparent {
    border-color: var(--divider-color);
    color: var(--text-primary-color);
  }

  .pagemenu__btn--transparent:hover {
    background-color: var(--subheader-button-transparent-color);
  }

  .pagemenu__btn--transparent .pagemenu__btn--icon {
    border-right-color: var(--divider-color);
  }

  .pagemenu__btn--icon .fa-heart {
    color: var(--icon-color);
  }

  .pagemenu__btn--green {
    background: transparent;
    border: 1px solid var(--promobit-yellow);
  }

  .pagemenu__btn--green:hover {
    background: var(--promobit-yellow);
  }

  .pagemenu__btn--icon-green {
    border-right: 1px solid var(--promobit-yellow);
  }

  .pagemenu__btn--green:hover .pagemenu__btn--icon-green {
    border-right-color: #ffffff;
  }

  .pagemenu__btn--icon-green .fa-plus,
  .pagemenu__btn--offer {
