  }

  .pagemenu__btn--green:hover .pagemenu__btn--icon-green .fa-plus,
  .pagemenu__btn--green:hover .pagemenu__btn--offer {
    color: #ffffff;
  }

  /* Offer Timeline */
  .pr-tl .pr-tl-banner h1 {
    color: var(--text-secondary-color);
  }

  .tooltip-last-offers {
    color: var(--text-secondary-color);
  }

  .tooltip-last-offers:hover {
    color: var(--text-tertiary-color);
  }

  .timeline-opt__btn {
    background-color: var(--general-button-color);
    border-color: var(--divider-color);
    color: var(--text-secondary-color);
  }

  .timeline-opt__btn:hover {
    background-color: var(--divider-color);
  }

  .pr-tl .pr-tl-card,
  .pr-tl .pr-tl-card .product_image,
  .pr-tl .pr-tl-card .rating {
    background-color: var(--timeline-card-color);
  }

  .pr-tl .pr-tl-card .info .oldprice,
  .pr-tl .pr-tl-card .where,
  .pr-tl .pr-tl-card .info .comments-box {
    color: var(--text-tertiary-color);
  }

  .pr-tl .list .pr-tl-card {
    border-top: 10px solid var(--timeline-card-color);
  }

  .pr-tl .pr-tl-card .bottom_title {
    background-color: var(--divider-color);
    border-bottom: 1px solid var(--divider-color);
  }

  .pr-tl .pr-tl-card .bottom_title h2 a {
    color: var(--text-secondary-color);
  }

  .pr-tl .pr-tl-card .bottom_title:hover {
    background-color: var(--timeline-card-title-color);
  }

  .pr-tl .pr-tl-card .bottom_title:hover .pr-tl .pr-tl-card .bottom_title h2 {
    color: var(--text-primary-color);
  }

  .pr-tl .pr-tl-card .info .price {
    color: var(--promobit-yellow);
  }

  .pr-tl .pr-tl-card .cardpub .name {
