@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://moodle"), url-prefix("http://moodle") {

/** huji addition **/
.custom-select {
    color: #c0c0c0;
}

p {
    color: #c0c0c0 !important;
}

button,
[type="button"],
[type="reset"],
[type="submit"] {
    -webkit-appearance: button;
    color: #c0c0c0;
}

.nav-tabs .nav-link.active,
.nav-tabs .nav-item.show .nav-link {
    color: #c0c0c0;
    background-color: #404040;
    border-color: #c0c0c0 #c0c0c0 #c0c0c0;
}


.table th,
table.collection th,
table.flexible th,
.generaltable th,
.table td,
table.collection td,
table.flexible td,
.generaltable td {
    padding: .75rem;
    vertical-align: top;
    color: #c0c0c0 !important;
    border-top: 1px solid #c0c0c0;
}

.page-header-headings span {
    position: relative;
    line-height: 24px;
    vertical-align: middle;
    font: 32px medium !important;
    font-family: sans-serif !important;
    color: #c0c0c0 !important;
}

#page-wrapper #page-footer {
    flex-shrink: 0;
    background-color: #404040 !important;
    color: #c0c0c0 !important;
}

*,
*::before,
*::after {
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    border: #f0ad4e;
    background: #404040 !important;
}

#region-main {
    overflow-x: auto;
    overflow-y: visible;
    border: 1px solid #f0ad4e;
    border-radius: .25rem;
    padding: 1.25rem;
    background-color: #404040 !important;
}


#frame2-page {
    border: 5px solid #f0ad4e;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    border-bottom-left-radius: 10px;
    border-bottom-right-radius: 10px;
    background: #404040;
}



#page-header div.flex-wrap {
    background-color: #404040;
    color: #c0c0c0;
    background: #404040 !important;
}

#shnaton span {
    color: #c0c0c0 !important;
}

#page-header div.d-flex {
    border: 1px solid;
    border-color: #f0ad4e;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
}

.navbar-light .navbar-nav .nav-link,
.navbar-bootswatch .navbar-nav .nav-link {
    color: #c0c0c0;
}

.navbar-light .navbar-brand,
.navbar-bootswatch .navbar-brand {
    color: #c0c0c0;
}

/** end huji addition **/
body {
    background-color: #404040;
    color: #c0c0c0 !important;
}

/** Login Page **/
.breadcrumb {
    background-color: #333333;
}

.breadcrumb > li {
    text-shadow: none;
}

input,
input[type="text"],
input[type="password"] {
    background-color: #333333;
    color: #c0c0c0;
}

input[type="submit"] {
    background-color: #333333;
    color: #c0c0c0;
    background-image: none;
    text-shadow: none;
}

/** Upper bar **/
.navbar-inner {
    background-color: #2A2A2A;
    background-image: none;
}

/* Upper bar - Text heading */
.navbar .brand {
    text-shadow: none;
}

.block {
    background-color: #333333;
}

.block {
    background-color: #333333;
}

/** Courses **/
.courses .coursebox,
.courses .coursebox.even {
    background-color: #333333;
}

.courses .coursebox:hover,
.courses .coursebox.even:hover {
    background-color: #4c4c4c;
}

/* text */
a {
    color: #7FDBFF;
}

a:hover {
    color: #0092db;
}

.text_to_html > p:nth-child(8) > span:nth-child(2) {
    color: #c0c0c0;
}

/* Course List: XX YYY - XX YYY row */
.course-content .current {
    background-color: #4c4c4c;
}

/** Homework/Assignment **/
/* Grading Summary */
.generaltable tbody > tr:nth-child(2n+1) > td {
    background-color: #404040;
}

.generaltable tbody tr:hover > td {
    background-color: #4c4c4c;
}

/* Submissions Page */
select {
    background-color: #333333;
    color: #c0c0c0;
}

/* rows */
.path-mod-assign .gradingtable tr.unselectedrow td {
    background-color: #404040;
}

#fitem_id_submitbutton {
    background-color: #404040;
}

/* row links */
.path-mod-assign .gradingtable tr.unselectedrow td a {
    color: #0092db;
}

.path-mod-assign .gradingtable tr.unselectedrow td a:hover {
    color: #29b8ff;
}

/* 'Edit' dropdown */
.moodle-actionmenu.show[data-enhanced] .menu {
    background-color: #404040;
}

.moodle-actionmenu.show[data-enhanced] .menu a {
    color: #c0c0c0;
}

/* 'Feedback Comments' */
textarea {
    background-color: #333333;
    color: #c0c0c0;
}

/** Forum/Discussion Posts **/
/* Post */
.forumpost {
    background-color: #404040;
}

/* Message */
div.editor_atto_toolbar,
div.editor_atto_toolbar div.atto_group {
    background-color: #404040;
}

.editor_atto_content_wrap {
    background-color: #333333;
    color: #c0c0c0;
}

/* Attachments */
.filemanager .fp-navbar,
.fp-viewbar,
.fp-toolbar .fp-btn-add,
.filemanager-container {
    background-color: #404040;
}

.dndupload-arrow {
    filter: invert(75%);
}

.filemanager-container .dndupload-target {
    background-color: #333333;
}

.filemanager-container {
    background: #333333;
}

/* 'Post to Forum', 'Cancel' Pane */
#fgroup_id_buttonar {
    background-color: #404040;
}

/** Attachments upload - File Picker - Recent files **/
/* Top Header */
.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd,
.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd.yui3-widget-hd {
    background: #333333;
    color: #c0c0c0;
    text-shadow: none;
}

/* Left Column */
.moodle-dialogue-base .moodle-dialogue-wrap {
    background-color: #333333;
}

.file-picker a {
    color: #c0c0c0;
}

/* Selected row */
.file-picker .fp-list .fp-repo.active {
    background-color: #404040;
    color: #c0c0c0;
}

/* Right Top Bar */
.file-picker .fp-navbar {
    background-color: #333333;
}

/* Right Bottom Area */
.file-picker .fp-content,
.fp-iconview .fp-filename-field .fp-filename {
    background-color: #404040;
}

/* Icon/Grid View */
.fp-iconview .fp-filename-field .fp-filename:hover {
    color: #e6e6e6;
}

/* List View */
.yui3-skin-sam .yui3-datatable-header {
    background-color: #404040;
    color: #c0c0c0;
    text-shadow: none;
}

.moodle-dialogue-base .moodle-dialogue .moodle-dialogue-bd {
    color: #e6e6e6;
}

.yui3-skin-sam .yui3-datatable-even .yui3-datatable-cell {
    background-color: #404040;
}

.yui3-skin-sam .yui3-datatable-odd .yui3-datatable-cell {
    background-color: #333333;
}

/** Attachments upload - File Picker - Upload a file **/
.fp-content-center form + div {
    background-color: #404040;
}
}

