@-moz-document domain("getmusicbee.com"), domain("musicbee.fandom.com"), domain("fandom.com") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. ______________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}

@-moz-document domain("getmusicbee.com"), domain("musicbee.fandom.com"), domain("fandom.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

.wikia-gallery-item .thumb .gallery-image-wrapper {
    overflow: visible!important;
}

.WikiaArticle pre {
    line-height: 22px!important;
    white-space: pre-line!important;
    word-wrap: break-word!important;
}

.addon_similar .addon_similar_wrap h2,
.btn.btn_download,
.codeheader,
.keyinfo .smalltext,
.mainmenu .menu_wrapper .menu_position li a,
.pi-section-label,
.table_list tbody.header td.catbg,
.wds-button,
.wds-global-navigation__link,
.wds-tabs__tab-label,
h3.catbg,
h3.catbg2,
h4.catbg,
h4.catbg2
{
	text-transform: capitalize!important;
}

.LightboxModal .LightboxCarousel .LightboxCarouselContainer li.active,
.SpeechBubble .speech-bubble-message,
.SpeechBubble .speech-bubble-message:before,
.WikiaArticle .toc,
.WikiaArticleComments .MiniEditorWrapper .editarea,
.addon_list_box li .addon_list_box_wrapper,
.addon_list_box li .addon_list_box_wrapper:hover,
.ambox,
.article-categories,
.bbc_link:link,
.bbc_link:visited,
.codeheader,
.description,
.description_board,
.hljs,
.information,
.input,
.pagelinks,
.plainbox,
.register_error,
.screenshot ul .screenshot_zoom_click,
.screenshot ul .screenshot_zoom_click .screenshot_wrapper,
.smalltext,
.stat_meter li,
.table_grid tr.catbg,
.table_grid tr.titlebg,
.thumb > .accent,
.topic_table table thead,
.ve-ui-mwSaveDialog-options,
.ve-ui-mwSaveDialog-summary,
.wds-global-navigation__link-group .wds-dropdown__content .wds-button.wds-is-secondary,
.wds-global-navigation__notifications-dropdown .wds-dropdown__content .wds-button.wds-is-secondary,
.wds-global-navigation__user-menu .wds-dropdown__content .wds-button.wds-is-secondary,
:focus,
a.navPages,
blockquote.bbc_alternate_quote,
blockquote.bbc_standard_quote,
input[type='date'],
input[type='date']:focus,
input[type='email'],
input[type='email']:focus,
input[type='password'],
input[type='password']:focus,
input[type='text'],
input[type='text']:focus,
pre,
table,
tbody,
tr.approvebg td,
tr.highlight2 td,
tr.smalltext th,
tr.windowbg td,
tr.windowbg2 td
{
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

.bbc_img {
	margin: 0 10rem 15rem!important;
}

.bbc_img:hover {
    transform: scale(1.5)!important;
}

/*___ Fonts ________________________________________________________________________*/

.btn,
.keyinfo .smalltext,
.mainmenu .menu_wrapper .menu_position li a,
.navigate_section ul li a,
.show_info .description,
.show_info,
.wds-global-navigation,
.wds-global-navigation__link,
body,
h1,
h2,
h3,
h4
{
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

selector {
	font-size: 21px!important;
}

.WikiaPage .WikiaArticle,
.addon_list_box li .addon_list_box_wrapper .addon_list_box_info a .title,
.codeheader,
.markdownView p,
body,
input,
select,
textarea
{
	font-size: 19px!important;
}

.wds-button, .wds-dropdown__content .wds-list.wds-is-linked .wds-dropdown-level-2 .wds-dropdown-level-2__content .wds-list.wds-is-linked>li>a,
.show_info .description,
.small_info.active,
.wikia-gallery-item .lightbox-caption,
.wikia-gallery-item .title
{
    font-size: 18px!important;
}

.pi-section-label,
.quoteheader,
.topic_table td.lastpost,
.wds-tabs__tab-label
{
	font-size: 17px!important;
}

blockquote.bbc_alternate_quote,
blockquote.bbc_standard_quote
{
	font-size: 19px!important;
	line-height: 27px!important;
}

selector {
	line-height: 19px!important;
}

.screenshot ul .screenshot_zoom_click .screenshot_wrapper {
    height: 16rem!important;
}

/*___ Remove Items _________________________________________________________________*/

.SpeechBubble .speech-bubble-message:before,
.WikiaBarWrapper,
.WikiaFooter,
.wds-global-footer
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.addon_list_box li .addon_list_box_wrapper .love {
    top: 8rem!important;
    left: 6rem!important;
}

.wikia-gallery-item .thumb .lightbox-caption {
    bottom: -29px!important;
}

/*___ Margins ______________________________________________________________________*/

.addon_similar .addon_similar_wrap .addon_similar_inline_wrap,
.wikia-gallery-item,
.addon_list_box li,
.main_content_wrapper.col_2_1 .sub_content_wrapper:nth-child(1),
.main_content_wrapper.col_2_1 .sub_content_wrapper:nth-child(2),
.main_panel .content.mb_download_installer_bg .download_img_link_wrapper,
.windowbg2
{
	margin: 12px!important;
}

.wikia-gallery-item .thumb a.image, .wikia-gallery-item .thumb a.image-no-lightbox {
	margin: 0 auto!important;
}


.markdownView p {
	margin: 1rem!important;
}

dl.register_form dd {
    margin: 0.5em 0 0 -22rem!important;
}

/*___ Paddings _____________________________________________________________________*/

.addon_similar .addon_similar_wrap .addon_similar_inline_wrap,
.wikia-gallery-item,
#upshrinkHeaderIC,
.WikiaRail,
.addon_list_box li,
.register_error
{
	padding: 12px!important;
}

selector {
	padding: 6px!important;
}

/*___ Heights ______________________________________________________________________*/

selector {
	max-height: none!important;
}

.wikia-gallery-item .thumb {
	height: 16rem!important;
}

/*___ Widths _______________________________________________________________________*/

#content_section,
.addon_similar .addon_similar_wrap,
.main_content_wrapper,
.main_panel .content.mb_download_installer_bg .sub_content
{
	max-width: none!important;
}

selector {
	width: auto!important;
}

.wikia-gallery-item .thumb a.image,
.wikia-gallery-item .thumb a.image-no-lightbox {
    width: 100%!important;
}

.WikiaPage,
.main_panel .content.mb_download_installer_bg .sub_content,
.wds-community-header,
form#registration
{
	width: 98%!important;
}

.WikiaMainContent {
    width: calc(98% - 320px)!important;
}

.main_panel .content.mb_download_installer_bg .download_img_link_wrapper {
    width: 57%!important;
}

.main_content_wrapper.col_2_1 .sub_content_wrapper:nth-child(1) {
    width: 63.666667%!important;
}

.addon_similar .addon_similar_wrap.col_2_1 .addon_similar_inline_wrap:nth-child(1) {
    width: 62%!important;
}

.main_panel .content.mb_download_installer_bg .sub_content .installer_panel_wrapper {
    width: 39%!important;
}

.main_content_wrapper.col_2_1 .sub_content_wrapper:nth-child(2) {
    width: 31.333333%!important;
}

.wikia-gallery-spacing-medium .wikia-gallery-item {
    width: 29%!important;
}

.addon_list_box li {
    width: 18%!important;
}

.WikiaRail {
    width: 17%!important;
}
}

@-moz-document domain("getmusicbee.com"), domain("musicbee.fandom.com"), domain("fandom.com") {
/*__________________________________________________________________________________*/
