.teased-wrapper,
div.talk-stream-comment-wrapper {
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

#right-rail .chain-content .pb-f-posttv-powa-playlist-item:hover,
.border-top-off.border-top-100-pct.border-bottom-hairline.border-bottom-100-pct:hover,
.cb:hover,
.moat-trackable.pb-f-theme-normal.pb-f-dehydrate-false.pb-f-async-false.col-md-6.col-sm-6.col-xs-12.pb-feature.pb-layout-item.pb-f-homepage-story-ans.col-lg-6.first-in-chain-row:hover,
.pb-f-homepage-card-ans .panel:hover,
.pb-f-homepage-chat-schedule ul li:hover,
.pb-f-homepage-story-ans.pb-feature:hover,
.pb-f-page-market-watch .market-diff>div:hover,
.teased-wrapper:hover,
div.talk-stream-comment-wrapper:hover {
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

img {
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/

li,
p,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

li:hover,
p:hover,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

/*__________________________________________________________________________________*/

selector {
	opacity: .8!important;
}

selector:hover {
	opacity: 1!important;
}
}

@-moz-document domain("washingtonpost.com") {
