[href="#comments-wrapper"] svg {
	background: url(https://image.ibb.co/n0iGJG/comment-48x48.png) no-repeat!important;
	box-sizing: border-box;
}

[href="#comments-wrapper"] svg {
	background-size: 22px!important;
	padding-left: 24px;
}

/* Home _______________________________________________________________*/

[href="/?tid=home_link_ss"] svg {
	background: url(https://image.ibb.co/hK4LCb/Home-48x48.png) no-repeat!important;
	box-sizing: border-box;
}

[href="/?tid=home_link_ss"] svg {
	background-size: 22px!important;
	padding-left: 24px;
}

