	background: url(https://i.ibb.co/mG6X6vr/the-washington-post-logo.png) no-repeat!important;
	box-sizing: border-box;
}

.pb-f-homepage-masthead .masthead>a.logo>svg {
	background-size: 23rem!important;
	padding-left: 26rem;
	height: 5rem;
}

/* Search _______________________________________________________________*/

.fa-search:before {
	content: '';
}

.fa-search:before {
	background: url(https://image.ibb.co/hVxuzR/search.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-search:before {
	background-size: 14px!important;
