css:
@-moz-document domain(answers.unity3d.com)
{

/*---------------------
/   Color and Links
---------------------*/
.background
{
	background : none !important;
	background-color : /*[[background]]*/ !important;
}
body, textarea
{
	background-color : /*[[background]]*/ !important;
	color : #AAA !important;
}
h2
{
	color : #888 !important;
}
a
{
	color : #3679DA !important;
}
.question-summary-wrapper a:visited
{
	color : #8048F7 !important     /* visited questions color */
}

/*---------------------
/     Hide Sections
---------------------*/
#logo, .icon, #ground, .user-about
{
	display : none !important;
}

#section-header
{
	display : none !important;
}

#sidebar-lower
{
	display : none !important;
}

#subscription_box
{
	display : none !important;
}

/*---------------------
/    Unity Answers
---------------------*/
.retag-question
{
	background : none !important;
}
.post-score, .comment-score
{
	background: none !important;
	color: #ccc !important;
}
#top-infobar
{
	background : none !important;
	background-color : /*[[background]]*/ !important;
}
.score
{
	color: #DDD !important;
}
.wmd-button-row
{
	background : none !important;
}
.boxC
{
	background-color : /*[[dark-background]]*/ !important;
	border-color : #444 !important;
}
.votes, .views
{
	background-color : /*[[light-background]]*/ !important;
	border-bottom: 1px solid /*[[dark-background]]*/ !important;
	border-right: 1px solid /*[[dark-background]]*/ !important;
	color: #DDD !important;
}
.add-comment-link
{
	background-color : /*[[light-background]]*/ !important;
	border-bottom: 1px solid /*[[dark-background]]*/ !important;
	border-right: 1px solid /*[[dark-background]]*/ !important;
	color: #FFF !important;
}
.status, .pager-button, .tags a
{
	border-bottom: 1px solid /*[[dark-background]]*/ !important;
	border-right: 1px solid /*[[dark-background]]*/ !important;
}
.paginator a
{
	border: none !important;
	border-bottom: 1px solid #111 !important;
	border-right: 1px solid #111 !important;
}

.gsc-input input.gsc-input     /* search bar */
{
	background : none #373737 !important;
	color : #AAA !important;
	
}
.accepted-answer
{
	background : none #494949 !important;
	color : #CCC !important;
}
.accepted-answer a
{
	color : #38E !important;
}
.comment
{
	background : /*[[background]]*/ !important;
}
.comment-text
{
	color : #888 !important;
}
.wmd-preview
{
	background-color : /*[[dark-background]]*/ !important;
}

/*---------------------
/    Code Specifics
---------------------*/
.answer-body pre, .question-body pre, .comment-text pre, .node-body pre
{
   background-color : /*[[dark-background]]*/ !important;  
}
li.L0, li.L1, li.L2, li.L3, li.L4, li.L5, li.L6, li.L7, li.L8, li.L9
{
   background-color : /*[[dark-background]]*/ !important;
}
@media screen
{
	.pln     /* plain */
	{
	   color: /*[[pln]]*/ !important;  
	}
	.str     /* string */
	{
		color: /*[[str]]*/ !important;
	}
	.kwd     /* keyword */
	{
		color: /*[[kwd]]*/ !important;
	}
	.com     /* comment */
	{
		color: /*[[com]]*/ !important;
	}
	.typ     /* type */
	{
		color: /*[[typ]]*/ !important;
	}
	.lit     /* literals */
	{
		color: /*[[lit]]*/ !important;
	}
	.pun     /* punctuation */
	{
		color: /*[[pun]]*/ !important;
	}
}
}
