css:
@-moz-document url-prefix(https://www.aozora.gr.jp/cards/) {
  body {
    max-width:/*[[width]]*/;
    margin: auto;
    /*[[font]]*/
    color: /*[[color]]*/;
    background-color: /*[[bgcolor]]*/;
  }
  h1{
    margin: 8% auto;
  }
  h2:before {
    margin-right: 1em;
    font-weight: bold;
  }
  .original_title:before {
    content: '原題:';
  }
  .author:before {
    content: '著者:';
  }
  .translator:before {
    content: '翻訳:';
  }
  .subtitle:before {
    content: '副題:';
  }
  .original_title,
  .author,
  .translator,
  .subtitle {
    float: left;
    margin: 0;
    padding-right: 2em;
    font-size: 100%;
    font-weight: normal;
  }
  .main_text {
    clear:both;
    font-size: /*[[fontsize]]*/;
    line-height: /*[[lineheight]]*/;
  }
    /*[[br]]*/
}
