css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

/*
  https://userstyles.org/styles/100302
  v1.6
*/

/*[[pinned]]*/
/*[[lists]]*/
/*[[groups]]*/
/*[[apps]]*/
/*[[interests]]*/
/*[[pages]]*/
/*[[developer]]*/
/*[[events]]*/
/*[[fundraisers]]*/
/*[[create]]*/
#hiddenNav      > ul
{
 overflow: hidden          !important;
 max-height: 0             !important;
 opacity: 0                !important;
 transform-origin: 0 0     !important;
 transform: scaleY(0)      !important;
 transition: max-height .2s /*[[close-delay]]*/s,
             transform .2s /*[[close-delay]]*/s,
             opacity .2s /*[[close-delay]]*/s !important;
}

#pinnedNav:hover      > ul,
#listsNav:hover       > ul,
#groupsNav:hover      > ul,
#appsNav:hover        > ul,
#interestsNav:hover   > ul,
#pagesNav:hover       > ul,
#developerNav:hover   > ul,
#eventsNav:hover      > ul,
#fundraisersNav:hover > ul,
#createNav:hover      > div,
#hiddenNav:hover      > ul
{
 max-height: 5000em       !important;
 opacity: 1               !important;
 transform: scaleY(1)     !important;
 transition: max-height .1s,
             transform .1s,
             opacity .1s !important;
}
    
}
