/**
 * MingLan Style for Firefox（火狐明蘭體樣式）
 *
 * 使用「明蘭體（MingLan）」取代 Firefox 中日韓的預設字型。需要先安裝明蘭體字型。
 * 修改自 t7yang 製作的 MSJhengHei Style v1.8：http://pastebin.com/cTkZTBD1
 */

@charset "UTF-8";
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("((?!chrome://|telnet://).)*") {
    /* 一般字型 */
    /*[[sans-serif]]*/
    /*[[serif]]*/
    /*[[monospace]]*/

    /* 常見英文字型 */
    /*[[arial]]*/
    /*[[consolas]]*/
    /*[[courier]]*/
    /*[[courier-new]]*/
    /*[[tahoma]]*/
    /*[[times-new-roman]]*/
    /*[[verdana]]*/

    /* 中文字型 */
    /*[[pmingliu]]*/
    /*[[mingliu]]*/
    /*[[kaiti]]*/
    /*[[jhenghei]]*/
    /*[[yahei]]*/
    /*[[simhei]]*/
    /*[[simsun]]*/

    /* 日文字型 */
    /*[[ms-pgothic]]*/
    /*[[ms-gothic]]*/
    /*[[ms-mincho]]*/
    /*[[ms-pmincho]]*/
    /*[[meiryo]]*/
    /*[[meiryo-ui]]*/
}
