css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gendou.com") {
body, table {background:#f4f4f4;color:#000}
.lyricsBody {border:1px solid #444444}
.body {background-color:#f4f4f4;}
    .td {background-color:#f4f4f4 !important}
a:link {color: #C1433F !important;}
a:hover {color: #4488BB !important;}
a:visited {color: #C1433F !important;}
#navPanel a:link {color: #C1433F !important;}
#navPanel a:link {color: #C1433F !important;}
#navPanel a:visited {color: #C1433F !important;}
.new {background-color:#C1433F}
.lyricsTab {border:1px solid #444444;background:none}
.lyricsShown {background:#C1433F;color:#fff}
    
 .link {color: #C1433F;}
.navHorizontal li {background-color:#f4f4f4}
#navPanel {border:none;background:#f4f4f4}
.navHorizontal li:hover, .navHorizontal li.over, .navHorizontal a:hover {background-color:#f4f4f4;color:#4488BB !important}
#navPanel li {border-right:1px solid #444444}
#navPanel li:hover {border-right:1px solid #C1433F}
.navVertical {border:1px solid #444444}
.red {color:blue}
input, select, textarea {border:1px solid #444444}
.news {background:#f4f4f4;border:1px solid #444444}
font {color:#C1433F;}
/*[[lyrics]]*/
}
