css:
@-moz-document domain('facebook.com')
{
	/*About page*/
	#pagelet_about {
		color: black;   
	}
	/*Right-side activity notifications*/
	.tickerActivityStories {
   		color: black !important;
	}
	/*Color of text in security-tips screen*/
	span.description {
		color: black;
	}
	.phl {
		color: black !important;
	}
	/*Color of activity text in activities page*/
	._42ef, _51m- vTop _5ep5 {
		color: black !important;
	}
	/*Some posts*/
	.aboveUnitContent, .uiProfileBlockContent, .userContentWrapper {
		color: black   
	}
	/*Post edit history*/
	._2pie {
		color: black !important;
	}
	/*Photo comments and Messenger message text*/
	div.UFICommentContent, .fbPhotosPhotoCaption, .webMessengerMessageGroup, ._5rpb {
		color: black !important;
	}
	div.UIStandardFrame_Content {
		width: 100% !important;
	}
	/*Photo/Video page*/
	#fbPhotoPageHeader {
		margin-left: auto !important;
		margin-right: auto !important;
		width: 95% !important;
	}
	.fbPhotoContributor{
		margin-left: auto !important;   
	}
	#fbPhotoPageContainer:not(.uiLayer), .fbxPhoto:not(.uiLayer) {
		background: #FFF !important;
		outline: 2px white !important;
	}
	/*Background of the page*/
	body, .a, #pagelet_welcome_box, #pagelet_navigation, ._55y4, .navHeader, .homeSideNav, .sortableItem,
	._5afe {
		background-color: /*[[background]]*/ !important;
		color: white !important;
	}
	/*Spinner*/
	a._2jq5._p{
		outline: none !important;
		border: none !important;
	}
	/*Left Column text color*/
	#leftCol {
		color: white !important;
	}
	/*Account name and 'edit profile' links*/
	A.item.clearfix, .prs > div:nth-child(2) > a:nth-child(2),.fbxWelcomeBoxName {
		outline: none !important;
		border: none !important;
		color: white !important;
	}
	/*Left page links*/
	li.sideNavItem.stat_elem.open.open.selectedItem > A.item.clearfix{
		outline: none !important;
		border: none !important;
		color: white !important;
		background-color: #444444 !important;
	}
	/*Hovered left page links*/
	li.sideNavItem.stat_elem.open.open.selectedItem > A.item.clearfix:hover,
	A.item.clearfix:hover, .sortableItem:hover, ._5afe:hover {
		outline: none !important;
		border: none !important;
		color: white !important;
		background-color: #666666 !important;
	}
	/*Main Container*/
	#globalContainer {
		margin-left: auto !important;
		margin-right: auto !important;
	}
	/*News area backgrounf*/
	div#contentCol.homeWiderContent,
	div#contentCol.clearfix.hasRightCol.homeWiderContent.homeFixedLayout {
		background: /*[[news_background]]*/ !important;
	}
	/*Post area*/
	#contentCol {
        width:auto !important;
	}
	/*Timeline container*/
	#pagelet_main_column_personal {
		margin-left: 17.5% !important;
		margin-right: -17.5% !important;
		width: auto !important;
	}
	#timeline_tab_content
	{
		margin-left: -17.5% !important;
	  	width: 100%;
	}
	#timeline-medley
	{
		margin-left: -17% !important;
		width: 100% !important;
	}
	/*Timeline background color*/
	#timeline_tab_content > * {
		background-color: /*[[timeline_background]]*/ !important;
	}
	/*Various titles egx. timeline dividers*/
	h3 {
		color: #BBC !important;
		background: none !important;
		text-shadow: none !important;
	}
	/*Timeline divider*/
	div._5vg {
		border: none !important;
	}
	/*Timeline divider button*/
	div._6a.uiPopover.uiHeaderActions, div._6a.uiPopover.uiHeaderActions > * {
		background: none !important;
		text-shadow: none !important;
	}
	/*Right column*/
	#rightCol {
		display: none !important;
	}
	/*Post area*/
	#contentArea {
		margin-left: auto !important;
		margin-right: auto !important;
		width: auto !important;
		max-width: 99% !important;
	}
	/*Recommended pages area*/
	div._4-u2.mvm {
		width: 100% !important;
		margin-left: auto !important;
		margin-right: auto !important;
	}
	/*Various swf, flash, youtube posts content*/
	iframe {
		width: 100% !important;
		height: 100% !important;
		margin: 0 !important;
		padding: 0 !important;
	}
	/*Various link images, videos, photos and photo grids*/
	a._4-eo {
		margin-right: auto !important;
		margin-left: auto !important;
	}
	div._6l-.__c_ {
		margin-right: 20% !important;
		margin-left: 20% !important;
	}
	div._2a2q {
		margin-right: auto !important;
		margin-left: auto !important;
	}
	div._6n_ {
		margin-left: auto !important;
		margin-right: auto !important;
	}
	/*Apps page*/
	div._4-u2._1hld {
		width: 100% !important;
		right: 0 !important;
	}
	/*Various lists (apps, friend groups etc.)*/
	div._4-u2._1hld .item.clearfix:hover, div._4-u2._1hld .item.clearfix:hover * {
		background-color: white !important;
		text-decoration: underline !important;
	}
	div._4-u2._1hld * {
		color: black;
	}
	div._4-u2._1hld:hover * {
		color: black;
		background-color: white !important;
	}
	#pagelet_seeall_filter * {
		background: white !important;
	}
	#pagelet_seeall_filter div:hover {
		text-decoration: underline !important;
	}
	._1hld {
		width: 100% !important;
		margin-right: -moz-calc(57.5%) !important;
	}
	._4ki, ._4bl7 {
		color: black !important;
	}
}
