css:
@-moz-document url-prefix("http://mazmorra.com.ar/chat"), url-prefix("http://www.mazmorra.com.ar/chat") {
.userinfo                               {
                                            background-color: /*[[fondouserinfo]]*/!important;
                                            color: /*[[frenteuserinfo]]*/!important;
                                        }

.userinfo .desc,
.num                                    {
                                            color: /*[[highlight_pv]]*/!important
                                        }

.bodycontainer,
.pvbody                                 {
                                            background: /*[[fondo]]*/!important;
                                            color: /*[[frente]]*/!important;
                                        }

.join                                   {
                                            background: /*[[fondo_jp]]*/!important;
                                            color: /*[[join]]*/!important;
                                        }

.lobbycontainer,
.chatinfo,
.chatexcerpt,
.chatroomscontainer                     {
                                            background: /*[[fondo_lobby]]*/!important;
                                            color: /*[[frente_lobby]]*/!important;
                                        }

.message p img:not(.emoji)              {
                                            opacity: 0.2!important
                                        }

.message p img:not(.emoji):hover        {
                                            opacity: 1.0!important
                                        }

.part                                   {
                                            background: /*[[fondo_jp]]*/!important;
                                            color: /*[[part]]*/!important;
                                        }

.roomuserlist                           {
                                            color: /*[[nicks]]*/!important
                                        }

.roomuserlist .selected,
.roomuserlist .selected .usernamecontainer
                                        {
                                            background: /*[[fondo_nicks_sel]]*/!important;
                                            color: /*[[nicks_sel]]*/!important;
                                        }

.roomuserlist .usernamecontainer:hover  {
                                            background: /*[[fondo_nicks_sel]]*/!important
                                        }

.roomuserlist,
.usernamecontainer,
.usercontainer,
.timestamp,
.usermention,
.roomuserlist ul li                     {
                                            background: /*[[fondo_nicks]]*/!important
                                        }

.textbox                                {
                                            background: /*[[borde_textbox]]*/!important
                                        }

.textbox input                          {
                                            background: /*[[fondo_textbox]]*/!important;
                                            color: /*[[frente_textbox]]*/!important;
                                        }

.timestamp                              {
                                            color: /*[[timestamp]]*/!important
                                        }

.usermention                            {
                                            color: /*[[mencion]]*/!important
                                        }
}
