css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

	/* Main body */
	
	body {
		font-size: 10pt;
		font-family: /*[[ssfont]]*/, sans-serif;
	}

	#contentArea {
		margin-left: 40px;
		width: 76%;
	}

	
	/* Attached content */
	
	#mtm {
		text-align: center;
	}

	.fbStoryAttachmentImage, .uiScaledImageContainer, .mtm, ._4-eo, #userContent,  ._5cq3, ._52c6 {
		text-align: center;
		margin-left: auto;
		margin-right: auto 
	}

	.fbStoryAttachmentImage, .uiScaledImageContainer {
		width: 100%;
		height: 100%;
	}

	
	/* Columns */
	
	ego_section {
		display: none;
	}

	#rightCol {
		display: none;
	}

	#u_58_0.ego_section {
		display: none;
	} 

	#pagelet_ego_pane {
		display: none;
	}

	#appsNav {
		display: /*[[displayapp]]*/ !important;
	}

	#listsNav {
		display: /*[[displaylists]]*/  !important;
	}

	#interestsNav {
		display: /*[[displayinterests]]*/  !important;
	}

	#pagesNav {
		display: /*[[displaynav]]*/  !important;
	}

	#developerNav {
		display: none;
	}
	 


	 
	/* User name */
	
	.fbxWelcomeBoxName {
		font-family: /*[[ssfont]]*/;
		font-size: 11.2pt;
		font-weight: 500;
	}

	.fbxWelcomeBoxName:hover {
		text-decoration: none;
	}

	#pagelet_welcome_box:hover {
		background: #eee;
	}

	
	/* Edit profile */
	
	._6a._6b {
		font-family: /*[[ssfont]]*/;
		font-size: 8pt;
		font-weight: 300;
	}

	
	/* Left bar */
	
	#leftCol {
		font-family: /*[[ssfont]]*/;
		font-size: 10pt;
		font-weight: 500;
	}
	  

	/* News Feed */
	
	.userContent {
		font-family: /*[[ssfont]]*/;
		font-size: 14pt;
		font-weight: 300;
	} 

	#stream_pagelet {
		width: 130%;
	}

	#pagelet_group_ {
		width: 130%;
	}

	#pagelet_timeline_main_column {
		width: 100%;
	}

	#headerArea {
		margin-left: 40px;
	}

	#wmMasterView {
		float: left;
	}

	#_2nb {
		float: right;
		max-width: 10%;
	}


	/* User and page names */
	
	._5pw, .fwb.fcg, .fwb, a[id*="js_5"], .profileLink, a[id*="js_"] {
		font-family: /*[[ctfont]]*/;
		font-weight: 400;
	}

	
	/* Link title and description */
	
	.mbs._6m6 {
		font-family: /*[[ssfont]]*/;
		font-size: 14pt;
		letter-spacing: 0.5pt;
	} 

	._6ma {
		font-family: /*[[wsfont]]*/;
		font-size: 10pt;
		text-align: justify;
		margin-left: auto;
		margin-right: auto;
		font-weight: 100;
	} 
	  
	  
	/* Container actions */
	
	.UFILikeLink, *[class*="uiLinkButton"], .share_action_link {
	  font-family: /*[[ctfont]]*/;
	  font-weight: 400;
	  font-size: 10pt;
	  margin: auto;
	}
	  
	  
	/* Similar pages */
	
	._4-u2.mvm {
		display: none;
	}


	/* Chat */
	
	.fbChatOrderedList {
		font-family: /*[[ssfont]]*/;
		font-size: 11.2pt;
		font-weight: 300;
	}

	
	/* Feed */
	
	.tickerFeedMessage {
		font-family: /*[[ssfont]]*/;
		font-size: 11.2pt;
		font-weight: 300;
	}
	

	/* Groups */
	
	.groupHugFrame {
		display: none;
	}

	#groupsJumpTitle {
		font-family: /*[[ssfont]]*/;
		font-size: 16pt;
		font-weight: 300;
	}
	
}
